mapper.sql:
select c.orderDtl_child_id, sum(a.order_count) order_count from vmes_assist_discard_detail a left join vmes_assist_discard b on a.parent_id = b.id left join vmes_assist_deliver_detail_child c on a.deliver_dtl_child_id = c.id
and a.order_id = #{orderId}
and a.order_dtl_id = #{orderDtlId}
and b.state = '3'
and b.type = '2'
group by c.orderDtl_child_id

select c.id orderDtl_child_id, round((ifnull(c.ratio, 0) * ifnull(sum(a.order_count), 0)),2) order_count from vmes_assist_discard_detail a left join vmes_assist_discard b on a.parent_id = b.id left join vmes_assist_order_detail_child c on a.order_dtl_id = c.order_dtl_id
and a.order_id = #{orderId}
and a.order_dtl_id = #{orderDtlId}
and b.state = '3'
and b.type = '1'
group by c.id

select orderChild.id orderDtl_child_id, round((ifnull(orderChild.ratio, 0) * (ifnull(signDtl.sign_fine_count, 0) + ifnull(signDtl.discard_count, 0))),2) order_count from ( select order_detail_id, sum(sign_fine_count) sign_fine_count, sum(discard_count) discard_count
from vmes_assist_sign_detail
and order_id = #{orderId}
and order_detail_id = #{orderDtlId}
and state = '2'
group by order_detail_id ) signDtl left join vmes_assist_order_detail_child orderChild on signDtl.order_detail_id = orderChild.order_dtl_id

select orderChild.id orderDtl_child_id, round((ifnull(orderChild.ratio, 0) * ifnull(signDtl.sign_fine_count, 0)),2) order_count from ( select order_detail_id, sum(sign_fine_count) sign_fine_count from vmes_assist_sign_detail
and order_id = #{orderId}
and order_detail_id = #{orderDtlId}
and state = '2'
group by order_detail_id ) signDtl left join vmes_assist_order_detail_child orderChild on signDtl.order_detail_id = orderChild.order_dtl_id

select orderChild.id orderDtl_child_id, round((ifnull(orderChild.ratio, 0) * ifnull(signDtl.discard_count, 0)),2) order_count from ( select order_detail_id, sum(discard_count) discard_count from vmes_assist_sign_detail
and order_id = #{orderId}
and order_detail_id = #{orderDtlId}
and state = '2'
group by order_detail_id ) signDtl left join vmes_assist_order_detail_child orderChild on signDtl.order_detail_id = orderChild.order_dtl_id

select deliverChild.orderDtl_child_id, round(ifnull(retreatDtl.order_count, 0), 2) order_count from ( select a.deliver_dtl_child_id, sum(a.order_count) order_count from vmes_assist_retreat_detail a left join vmes_assist_retreat b on a.parent_id = b.id
and a.order_id = #{orderId}
and a.order_dtl_id = #{orderDtlId}
and b.state = '3'
and b.type = '2'
group by a.deliver_dtl_child_id ) retreatDtl left join vmes_assist_deliver_detail_child deliverChild on retreatDtl.deliver_dtl_child_id = deliverChild.id

select deliverChild.orderDtl_child_id, round(ifnull(retreatDtl.sign_fine_count, 0),2) order_count from ( select a.deliver_dtl_child_id, sum(a.sign_fine_count) sign_fine_count from vmes_assist_retreat_detail a left join vmes_assist_retreat b on a.parent_id = b.id
and a.order_id = #{orderId}
and a.order_dtl_id = #{orderDtlId}
and b.state = '3'
and b.type = '2'
group by a.deliver_dtl_child_id ) retreatDtl left join vmes_assist_deliver_detail_child deliverChild on retreatDtl.deliver_dtl_child_id = deliverChild.id

select b.orderDtl_child_id, sum(b.count) deliverCount from vmes_assist_deliver_detail a left join vmes_assist_deliver_detail_child b on a.id = b.deliver_dtl_id
and a.order_id = #{orderId}
and a.order_detail_id = #{orderDtlId}
and a.state = '1'
group by b.orderDtl_child_id
