mapper.select:
select md5(uuid()) id, deliverDtlChild.id deliverDtlChildId, deliverDtlChild.deliver_id deliverId, deliverDtlChild.deliver_dtl_id deliverDtlId, deliverDtlChild.out_dtl_id outDtlId, deliverDtlChild.in_dtl_id inDtlId,
deliverDtlChild.orderDtl_child_id orderDtlChildId, deliverDtlChild.product_id productId, deliverDtlChild.unit_id unitId, round(ifnull(deliverDtlChild.ratio, 0),2) ratio, round(ifnull(deliverDtlChild.count, 0),2) count,
deliverDtlChild.cuser cuser, deliverDtlChild.cdate cdate, deliverDtlChild.uuser uuser, deliverDtlChild.udate udate, deliverDtlChild.remark remark,
deliverDtlChild.isdisable isdisable,
product.code sysProductCode, product.source_code custProductCode, CASE WHEN product.source_code is null THEN product.code WHEN product.source_code is not null THEN product.source_code ELSE null END productCode, product.name productName, product.spec productSpec, product.genre productGenre, product.property productProperty, unitName.name unitName,
round(ifnull(retreat.order_count, 0),2) retreatCount, round(ifnull(discard.order_count, 0),2) discardCount, round((ifnull(retreat.order_count, 0) + ifnull(discard.order_count, 0)),2) retreatAndDiscardCount,
round((ifnull(deliverDtlChild.count, 0) - (ifnull(retreat.order_count, 0) + ifnull(discard.order_count, 0))) ,2) maxCount from vmes_assist_deliver_detail_child deliverDtlChild left join vmes_assist_deliver deliver on deliverDtlChild.deliver_id = deliver.id left join vmes_product product on deliverDtlChild.product_id = product.id left join vmes_dictionary unitName on deliverDtlChild.unit_id = unitName.id
left join ( select deliver_dtl_child_id, sum(order_count) order_count from vmes_assist_retreat_detail
and deliver_id = #{deliverId}
and parent_id in ( select id from vmes_assist_retreat
and company_id = #{currentCompanyId}
and supplier_id = #{supplierId}
and type = '2'
and state <> -1
)
group by deliver_dtl_child_id ) retreat on deliverDtlChild.id = retreat.deliver_dtl_child_id
left join ( select deliver_dtl_child_id, sum(order_count) order_count from vmes_assist_discard_detail
and deliver_id = #{deliverId}
and parent_id in ( select id from vmes_assist_discard
and company_id = #{currentCompanyId}
and supplier_id = #{supplierId}
and type = '2'
and state <> -1
)
group by deliver_dtl_child_id ) discard on deliverDtlChild.id = discard.deliver_dtl_child_id
and deliverDtlChild.deliver_id = #{deliverId}
order by ${orderStr}
