mapper.delete:
        delete from vmes_assist_deliver
        where id in

		#{item}

mapper.select:
select id id, company_id companyId, order_id orderId, supplier_id supplierId, make_id makeId,
sys_code sysCode, cust_code custCode, type type, amount amount, deliver_date deliverDate,
state state, waybill_company waybillCompany, waybill_code waybillCode, car_code carCode, supplier_mobile supplierMobile,
supplier_name supplierName, supplier_address supplierAddress, cuser cuser, cdate cdate, uuser uuser,
udate udate, remark remark, isdisable isdisable from vmes_assist_deliver
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select deliver.id id, deliver.company_id companyId, deliver.order_id orderId, deliver.supplier_id supplierId, deliver.make_id makeId,
deliver.sys_code sysCode, deliver.cust_code custCode, deliver.type type, deliver.amount amount, DATE_FORMAT(deliver.deliver_date,'%Y-%m-%d') deliverDate,
deliver.state state, deliver.waybill_company waybillCompany, deliver.waybill_code waybillCode, deliver.car_code carCode, deliver.supplier_mobile supplierMobile,
deliver.supplier_name supplierName, deliver.supplier_address supplierAddress, deliver.cuser cuser, deliver.cdate cdate, deliver.uuser uuser,
deliver.udate udate, deliver.remark remark, deliver.isdisable isdisable,
CASE WHEN deliver.state = '0' THEN '待发货' WHEN deliver.state = '1' THEN '已发货' WHEN deliver.state = '-1' THEN '已取消' ELSE null END stateName,
CASE WHEN deliver.type = '1' THEN '发货' WHEN deliver.type = '2' THEN '送货' WHEN deliver.type = '3' THEN '供应商自提' ELSE null END typeName,
assistOrder.sys_code orderCode, supplier.name supplier
from vmes_assist_deliver deliver left join vmes_customer supplier on deliver.supplier_id = supplier.id left join vmes_assist_order assistOrder on deliver.order_id = assistOrder.id
and deliver.id = #{deliverId}
and deliver.company_id = #{currentCompanyId}
and deliver.order_id = #{orderId}
and ${queryStr}
order by ${orderStr}
