mapper.select:
select md5(uuid()) id, detail.id id, detail.parent_id parentId, detail.plan_detail_id planDetailId, detail.assist_product_id assistProductId, detail.order_unit orderUnit,
round(ifnull(detail.order_count, 0),2) orderCount, round(ifnull(detail.loss_ratio, 0),2) lossRatio, round(ifnull(detail.price, 0), ) price, round(ifnull(detail.amount, 0),2) amount, DATE_FORMAT(detail.expect_date,'%Y-%m-%d') expectDate,
detail.state state, detail.cuser cuser, detail.cdate cdate, detail.uuser uuser, detail.udate udate,
detail.remark remark, detail.isdisable isdisable,
detail.parent_id orderId, detail.id orderDtlId,
assistProd.product_id productId, assistProd.product_name assistProductName, orderUnitName.name orderUnitName, craft.sys_code craftCode, craft.name craftName,
round(ifnull(retreat.order_count, 0),2) retreatCount, round(ifnull(discard.order_count, 0),2) discardCount, round((ifnull(retreat.order_count, 0) + ifnull(discard.order_count, 0)),2) retreatAndDiscardCount,
round((ifnull(detail.order_count, 0) - (ifnull(retreat.order_count, 0) + ifnull(discard.order_count, 0))) ,2) maxCount
from vmes_assist_order_detail detail left join vmes_assist_product assistProd on detail.assist_product_id = assistProd.id left join vmes_assist_craft craft on assistProd.craft_id = craft.id and craft.isdisable = '1' left join vmes_product_unit orderUnit on (assistProd.product_id = orderUnit.product_id and detail.order_unit = orderUnit.unit) left join vmes_dictionary orderUnitName on detail.order_unit = orderUnitName.id
left join ( select order_dtl_id, sum(order_count) order_count from vmes_assist_retreat_detail
and order_id = #{orderId}
and parent_id in ( select id from vmes_assist_retreat
and company_id = #{currentCompanyId}
and supplier_id = #{supplierId}
and type = '1'
and state <> -1
)
group by order_dtl_id ) retreat on detail.id = retreat.order_dtl_id
left join ( select order_dtl_id, sum(order_count) order_count from vmes_assist_discard_detail
and order_id = #{orderId}
and parent_id in ( select id from vmes_assist_discard
and company_id = #{currentCompanyId}
and supplier_id = #{supplierId}
and type = '1'
and state <> -1
)
group by order_dtl_id ) discard on detail.id = discard.order_dtl_id
and detail.parent_id = #{orderId}
order by ${orderStr}
