mapper.select:
select md5(uuid()) id, planDtl.id planDtlId, planDtl.parent_id parentId, planDtl.assist_product_id assistProductId, planDtl.order_unit orderUnit, planDtl.count count, planDtl.plan_date planDate, planDtl.state state, planDtl.isdisable isdisable,
assistProd.product_name assistProductName, prodUnit.unit productUnit, prodUnitName.name productUnitName,
craft.sys_code craftCode, craft.name craftName,
round(ifnull(supplier.price, 0), ) price, round(ifnull(supplier.loss_ratio, 0),2) lossRatio
from vmes_assist_plan_detail planDtl left join vmes_assist_product assistProd on planDtl.assist_product_id = assistProd.id and assistProd.isdisable = '1' left join vmes_assist_craft craft on assistProd.craft_id = craft.id and craft.isdisable = '1' left join vmes_product_unit prodUnit on assistProd.product_id = prodUnit.product_id and prodUnit.type = 1 left join vmes_dictionary prodUnitName on prodUnit.unit = prodUnitName.id left join ( select assist_product_id, price, loss_ratio from vmes_assist_supplier
and supplier_id = #{supplierId}
and company_id = #{companyId}
and isdisable = '1'
) supplier on assistProd.id = supplier.assist_product_id
and planDtl.parent_id in ( select id from vmes_assist_plan
and company_id = #{companyId}
and state in ('2','3','4')
and isdisable = '1'
)
