mapper.update:
update
set isdisable = '0' ,udate = now() where id in
#{item}

mapper.select:
select

from






                 and id <> #{id} 


                and id = #{id}







        and ${queryStr}



    order by ${orderStr}

select detail.id id, detail.parent_id parentId, detail.assist_product_id assistProductId, detail.order_unit orderUnit,
round(ifnull(detail.count,0),2) count, detail.plan_date planDate,
detail.state state, detail.cuser cuser, detail.cdate cdate, detail.uuser uuser, detail.udate udate,
detail.remark remark, detail.isdisable isdisable,
assistProd.product_name assistProductName, orderUnitName.name orderUnitName,
craft.sys_code craftCode, craft.name craftName from vmes_assist_plan_detail detail left join vmes_assist_product assistProd on detail.assist_product_id = assistProd.id and assistProd.isdisable = '1' left join vmes_assist_craft craft on assistProd.craft_id = craft.id and craft.isdisable = '1' left join vmes_dictionary orderUnitName on detail.order_unit = orderUnitName.id
and ${queryStr}
and detail.parent_id = #{parentId}
order by ${orderStr}

select detail.id id, detail.parent_id parentId, detail.assist_product_id assistProductId, detail.order_unit orderUnit,
round(ifnull(detail.count,0),2) count, detail.plan_date planDate,
detail.state state, detail.cuser cuser, detail.cdate cdate, detail.uuser uuser, detail.udate udate,
detail.remark remark, detail.isdisable isdisable,
assistProd.product_name assistProductName, orderUnitName.name orderUnitName,
craft.sys_code craftCode, craft.name craftName from vmes_assist_plan_detail detail left join vmes_assist_product assistProd on detail.assist_product_id = assistProd.id and assistProd.isdisable = '1' left join vmes_assist_craft craft on assistProd.craft_id = craft.id and craft.isdisable = '1' left join vmes_dictionary orderUnitName on detail.order_unit = orderUnitName.id
and ${queryStr}
and detail.parent_id = #{parentId}
order by ${orderStr}
