mapper.select:
select id id, company_id companyId, product_id productId, product_name productName, craft_id craftId,
cuser cuser, cdate cdate, uuser uuser, udate udate, remark remark,
isdisable isdisable from vmes_assist_product
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select assistProd.id id, assistProd.company_id companyId, assistProd.product_id productId, assistProd.product_name productName, assistProd.craft_id craftId,
assistProd.cuser cuser, assistProd.cdate cdate, assistProd.uuser uuser, assistProd.udate udate, assistProd.remark remark, assistProd.isdisable isdisable,
prodUnit.unit productUnit, prodUnitName.name productUnitName,
craft.sys_code craftCode, craft.name craftName from vmes_assist_product assistProd left join vmes_product product on assistProd.product_id = product.id left join vmes_product_unit prodUnit on assistProd.product_id = prodUnit.product_id and prodUnit.type = 1 left join vmes_dictionary prodUnitName on prodUnit.unit = prodUnitName.id
left join vmes_assist_craft craft on assistProd.craft_id = craft.id
and assistProd.company_id = #{currentCompanyId}
and assistProd.product_name = #{assistProductName}
and ( product.source_code like CONCAT('%',CONCAT(#{productValue}),'%') or product.code like CONCAT('%',CONCAT(#{productValue}),'%') or product.name like CONCAT('%',CONCAT(#{productValue},'%')) )
and product.spec LIKE CONCAT('%',CONCAT(#{productSpec}),'%')
and craft.sys_code LIKE CONCAT('%',CONCAT(#{craftCode}),'%')
and craft.name LIKE CONCAT('%',CONCAT(#{craftName}),'%')
and ${queryStr}
order by ${orderStr}
