mapper.delete:
        delete from vmes_assist_retreat_detail
        where id in

		#{item}

mapper.select:
select id id, parent_id parentId, order_id orderId, order_dtl_id orderDtlId, deliver_dtl_child_id deliverDtlChildId,
deliver_dtl_id deliverDtlId, deliver_id deliverId, assist_product_id assistProductId, product_id productId, order_unit orderUnit,
order_count orderCount, price price, amount amount, state state, cuser cuser,
cdate cdate, uuser uuser, udate udate, remark remark, isdisable isdisable,
quality quality, quality_type qualityType, quality_count qualityCount, bad_count badCount, discard_count discardCount,
receive_count receiveCount, quality_inDtl_id qualityInDtlId, receive_inDtl_id receiveInDtlId, sign_fine_count signFineCount, quality_fine_count qualityFineCount,
out_dtl_id outDtlId
from vmes_assist_retreat_detail
and id <> #{id}
and id = #{id}
and parent_id = #{parentId}
and assist_product_id = #{assistProductId}
and ${queryStr}
order by ${orderStr}

select
    id
    quality_inDtl_id qualityInDtlId,
    receive_inDtl_id receiveInDtlId
from vmes_assist_retreat_detail



        and quality_inDtl_id in (
            select id from vmes_warehouse_in_detail
            where parent_id = #{qualityInParentId}
        )




        and receive_inDtl_id in (
            select id from vmes_warehouse_in_detail
            where parent_id = #{receiveInParentId}
        )
