mapper.delete:
        delete from vmes_assist_retreat
        where id in

		#{item}

mapper.select:
select id id, company_id companyId, supplier_id supplierId, audit_id auditId, sys_code sysCode,
cust_code custCode, type type, amount amount, retreat_date retreatDate, state state,
cuser cuser, cdate cdate, uuser uuser, udate udate, remark remark,
isdisable isdisable from vmes_assist_retreat
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select retreat.id id, retreat.company_id companyId, retreat.supplier_id supplierId, retreat.audit_id auditId, retreat.sys_code sysCode,
retreat.cust_code custCode, retreat.type type, round(ifnull(retreat.amount, 0),2) amount, DATE_FORMAT(retreat.retreat_date,'%Y-%m-%d') retreatDate, retreat.state state,
retreat.cuser cuser, retreat.cdate cdate, retreat.uuser uuser, retreat.udate udate, retreat.remark remark,
retreat.isdisable isdisable,
CASE WHEN retreat.state = '0' THEN '待提交' WHEN retreat.state = '1' THEN '待审核' WHEN retreat.state = '2' THEN '待退货' WHEN retreat.state = '3' THEN '已完成' WHEN retreat.state = '-1' THEN '已取消' ELSE null END stateName, supplier.name supplierName, DATE_FORMAT(retreat.cdate,'%Y-%m-%d') creatDateStr, creatUser.user_name creatUserName, auditUser.user_name auditUserName
from vmes_assist_retreat retreat left join vmes_customer supplier on retreat.supplier_id = supplier.id left join vmes_user creatUser on retreat.cuser = creatUser.id left join vmes_user auditUser on retreat.audit_id = auditUser.id
and retreat.company_id = #{currentCompanyId}
and retreat.state = #{state}
and retreat.type = #{type}
and retreat.sys_code = #{sysCode}
and supplier.name LIKE CONCAT(CONCAT('%', #{supplierName}),'%')
and ${queryStr}
order by ${orderStr}
