mapper.select:
select signDtl.id id, signDtl.parent_id parentId, signDtl.order_id orderId, signDtl.order_detail_id orderDetailId, signDtl.in_detail_id inDetailId,
signDtl.assist_product_id assistProductId, signDtl.order_unit orderUnit, round(ifnull(signDtl.arrive_count, 0), 2) arriveCount, signDtl.quality quality, signDtl.quality_type qualityType,
round(ifnull(signDtl.quality_count, 0), 2) qualityCount, round(ifnull(signDtl.bad_count, 0), 2) badCount, round(ifnull(signDtl.retreat_count, 0), 2) retreatCount, round(ifnull(signDtl.discard_count, 0), 2) discardCount, round(ifnull(signDtl.receive_count, 0), 2) receiveCount,
signDtl.quality_inDtl_id qualityInDtlId, signDtl.receive_inDtl_id receiveInDtlId, signDtl.state state, round(ifnull(signDtl.sign_fine_count, 0), 2) signFineCount, round(ifnull(signDtl.quality_fine_count, 0), 2) qualityFineCount,
assistProd.product_id productId, assistProd.product_name assistProductName, orderUnitName.name orderUnitName, craft.sys_code craftCode, craft.name craftName,
round(ifnull(retreat.order_count, 0),2) retreatCount,
round((ifnull(signDtl.sign_fine_count, 0) - ifnull(retreat.order_count, 0)) ,2) maxRetreatCount
from vmes_assist_sign_detail signDtl left join vmes_assist_product assistProd on signDtl.assist_product_id = assistProd.id left join vmes_assist_craft craft on assistProd.craft_id = craft.id and craft.isdisable = '1' left join vmes_product_unit orderUnit on (assistProd.product_id = orderUnit.product_id and signDtl.order_unit = orderUnit.unit) left join vmes_dictionary orderUnitName on signDtl.order_unit = orderUnitName.id
left join ( select order_dtl_id, sum(order_count) order_count from vmes_assist_retreat_detail
and order_id = #{orderId}
and parent_id in ( select id from vmes_assist_retreat
and company_id = #{currentCompanyId}
and supplier_id = #{supplierId}
and type = '1'
and attribute = '2'
and state <> -1
)
group by order_dtl_id ) retreat on detail.id = retreat.order_dtl_id
and detail.parent_id = #{orderId}
order by ${orderStr}
