#{item}

mapper.select:
select

from




        where 1=2







                         and id <> #{id} 


                        and id = #{id}







                and ${queryStr}





    order by ${orderStr}

select

from
vmes_bom_tree tree
left join vmes_product product on product.id = tree.prod_id
left join vmes_dictionary genre on product.genre = genre.id
left join vmes_dictionary unit on product.unit = unit.id
left join vmes_dictionary type on product.type = type.id



        and ${queryStr}


        and product.code LIKE CONCAT(CONCAT('%', #{code}),'%')


        and product.name LIKE CONCAT(CONCAT('%', #{name}),'%')


        and tree.bom_id = #{bomId}


        and tree.layer = #{layer}


        and tree.parent_prod_id = #{parentProdId}



    order by ${orderStr}

select
from vmes_bom_tree tree left join vmes_product product on product.id = tree.prod_id left join vmes_dictionary genre on product.genre = genre.id left join vmes_dictionary unit on product.unit = unit.id left join vmes_dictionary type on product.type = type.id
and ${queryStr}
and product.code LIKE CONCAT(CONCAT('%', #{code}),'%')
and product.name LIKE CONCAT(CONCAT('%', #{name}),'%')
and (tree.prod_id = #{prodId} or tree.parent_prod_id = #{prodId})
and tree.bom_id = #{bomId}
order by ${orderStr}

select tree.id bomTreeId, tree.path_id pathId, tree.bom_id bomId, tree.prod_id id, product.name name, tree.parent_prod_id pid, tree.layer, tree.isdisable, 0 serialNumber from vmes_bom_tree tree left join vmes_product product on product.id = tree.prod_id
and product.name LIKE CONCAT(CONCAT('%', #{name}),'%')
and tree.bom_id = #{bomId}
and tree.parent_prod_id = #{pid}
and tree.prod_id = #{pid}
and tree.isdisable = #{isdisable}
and tree.path_id LIKE CONCAT(#{pathId},'%')
and ${queryStr}
