mapper.select:
select id id, company_id companyId, equipment_id equipmentId, content content, cuser cuser, cdate cdate, uuser uuser, udate udate, remark remark, isdisable isdisable from vmes_equipment_maintain_content
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select maintainContent.id id, maintainContent.company_id companyId, maintainContent.equipment_id equipmentId, maintainContent.content content, maintainContent.cuser cuser, maintainContent.cdate cdate, maintainContent.uuser uuser, maintainContent.udate udate, maintainContent.remark remark, maintainContent.isdisable isdisable,
equipment.code code, equipment.name name, equipment.spec spec,
department.name deptName, department.long_name deptPathName
from vmes_equipment_maintain_content maintainContent left join vmes_equipment equipment on maintainContent.equipment_id = equipment.id left join vmes_department department on equipment.dept_id = department.id
and maintainContent.company_id = #{currentCompanyId}
and maintainContent.equipment_id = #{equipmentId}
and maintainContent.isdisable = #{maintainContentIsdisable}
and maintainContent.content LIKE CONCAT(CONCAT('%', #{content}),'%')
and equipment.code LIKE CONCAT(CONCAT('%', #{code}),'%')
and equipment.name LIKE CONCAT(CONCAT('%', #{name}),'%')
and equipment.spec LIKE CONCAT(CONCAT('%', #{spec}),'%')
and equipment.isdisable = #{equipmentIsdisable}
and ${deptEqptQueryStr}
and ${queryStr}
order by ${orderStr}
