next_maintain_date nextMaintainDate, equipment_state equipmentState, begin_time beginTime, end_time endTime, cancel_reason cancelReason,
cancel_date cancelDate, cancel_user cancelUser, is_allow_maintain isAllowMaintain, is_valid_state isValidState, cuser cuser,
cdate cdate, uuser uuser, udate udate, remark remark, isdisable isdisable from vmes_equipment_maintain
and id <> #{id}
and id = #{id}
and company_id = #{companyId}
and equipment_id = #{equipmentId}
and maintain_content_id = #{maintainContentId}
and plan_id = #{planId}
and sys_begin_time = #{sysBeginTime}
and sys_end_time = #{sysEndTime}
and ${queryStr}
order by ${orderStr}
