mapper.delete:
        delete from vmes_equipment_repair
        where id in

		#{item}

mapper.select:
select id id, company_id companyId, equipment_id equipmentId, repair_task_id repairTaskId, sys_code sysCode,
warn_date warnDate, fault_describe faultDescribe, is_stop isStop, equipment_state equipmentState, begin_time beginTime,
end_time endTime, cancel_reason cancelReason, cancel_date cancelDate, cancel_user cancelUser, cuser cuser,
cdate cdate, uuser uuser, udate udate, remark remark, isdisable isdisable from vmes_equipment_repair
and id <> #{id}
and id = #{id}
and equipment_id = #{equipmentId}
and company_id = #{companyId}
and ${queryStr}
order by ${orderStr}

select repair.id id, repair.company_id companyId, repair.equipment_id equipmentId, repair.repair_task_id repairTaskId, repair.sys_code sysCode,
repair.warn_date warnDate, DATE_FORMAT(repair.warn_date,'%Y-%m-%d %T') warnDateTimeStr, repair.fault_describe faultDescribe, repair.is_stop isStop, repair.equipment_state equipmentState, CASE WHEN repair.equipment_state = '1' THEN '故障' WHEN repair.equipment_state = '2' THEN '维修中' WHEN repair.equipment_state = '3' THEN '已完成' ELSE null END equipmentStateName, repair.begin_time beginTime, DATE_FORMAT(repair.begin_time,'%Y-%m-%d %T') beginTimeStr,
repair.end_time endTime, DATE_FORMAT(repair.end_time,'%Y-%m-%d %T') endTimeStr, repair.cancel_reason cancelReason, repair.cancel_date cancelDate, repair.cancel_user cancelUser, repair.cuser cuser,
repair.cdate cdate, repair.uuser uuser, repair.udate udate, repair.remark remark, repair.isdisable isdisable,
task.task_state taskState, eqpt.code equipmentCode, eqpt.name equipmentName
from vmes_equipment_repair repair left join vmes_equipment_repairTask task on repair.repair_task_id = task.id left join vmes_equipment eqpt on repair.equipment_id = eqpt.id
and repair.equipment_id = #{equipmentId}
and repair.company_id = #{currentCompanyId}
and eqpt.code LIKE CONCAT(CONCAT('%', #{equipmentCode}),'%')
and eqpt.name LIKE CONCAT(CONCAT('%', #{equipmentName}),'%')
and repair.equipment_state = #{equipmentState}
and ${queryStr}
order by ${orderStr}
