mapper.select:
select id id, repair_id repairId, repair_user repairUser, fault_describe faultDescribe, time_length timeLength,
task_result taskResult, task_state taskState, task_type taskType, receive_date receiveDate, cancel_reason cancelReason,
cancel_date cancelDate, cancel_user cancelUser, retreat_type retreatType, cuser cuser, cdate cdate,
uuser uuser, udate udate, remark remark, isdisable isdisable from vmes_equipment_repairTask
and id <> #{id}
and id = #{id}
and repair_id = #{repairId}
and isdisable = #{isdisable}
and ${queryStr}
order by ${orderStr}

