mapper.delete:
        delete from vmes_equipment_sensor
        where id in

		#{item}

mapper.select:
select id id, company_id companyId, equipment_id equipmentId, target_type targetType, target_code targetCode,
target_name targetName, target_formula targetFormula, target_formula_column targetFormulaColumn, cuser cuser, cdate cdate,
uuser uuser, udate udate, remark remark, isdisable isdisable from vmes_equipment_sensor
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select equipment.id id, equipment.company_id companyId, equipment.qrcode qrcode, equipment.code code, equipment.name name, equipment.name_en nameEn, equipment.spec spec, equipment.type type, equipment.supplier supplier, equipment.dept_id deptId, equipment.buy_date buyDate, equipment.remark remark, equipment.cuser cuser, equipment.cdate cdate, equipment.uuser uuser, equipment.udate udate, equipment.isdisable isdisable, equipment.photo photo, department.name deptName, department.long_name deptPathName,
CASE WHEN equipmentSensor.sensorTargetCount is null or equipmentSensor.sensorTargetCount is null = 0 THEN '非联网设备' WHEN equipmentSensor.sensorTargetCount > 0 THEN '联网设备' ELSE null END bindSensorStateName,
'' businessState, '' businessStateName
from vmes_equipment equipment left join vmes_department department on equipment.dept_id = department.id left join ( select equipment_id, count(id) sensorTargetCount from vmes_equipment_sensor
and company_id = #{companyId}
and isdisable = #{equipmentSensorIsdisable}
group by equipment_id ) equipmentSensor on equipment.id = equipmentSensor.equipment_id
and equipment.company_id = #{companyId}
and equipment.dept_id = #{deptId}
and department.id_${layer} = #{pid}
and equipment.isdisable = #{equipmentIsdisable}
and ${queryStr}
