mapper.select:
select cdate,A0001,A0002,A0003 from vmes_sensor_source


        and equipment_id = #{equipmentId}


order by cdate asc

select
DATE_FORMAT(t.cdate,'%H:%i:%s') cdate
${colStr}
from ${viewStr} t


        and t.equipment_id = #{equipmentId}


        and ${queryStr}


order by t.cdate desc
