mapper.select:
select md5(uuid()) id, customer.id customerId, customer.genre, customer.code supplierCode, customer.name supplierName,
round(ifnull(beginDefault.begin_value,0), -1) defaultBeginValue,
round(ifnull(beginHistory.end_value,0), -1) historyBeginValue,
round(ifnull(nowBill.nowPlus,0),2) nowPlus,
round(ifnull(nowBill.nowMinus,0),2) nowMinus,
'' beginValue, '' beginPlus, '' beginMinus, '' endPlus, '' endMinus, '' endValue, '' period
from vmes_customer customer
left join ( select customer_id,begin_value from vmes_finance_base
and company_id = #{companyId}
and period = #{period}
and type = '1' and isdisable = '1'
) beginDefault on customer.id = beginDefault.customer_id
left join ( select customer_id,end_value from vmes_finance_history
and company_id = #{companyId}
and period = #{forePeriod}
) beginHistory on customer.id = beginHistory.customer_id
left join ( select a.id customer_id,
round((ifnull(b.amount,0) - ifnull(c.amount,0)),2) nowPlus,
round(ifnull(d.amount,0),2) nowMinus from vmes_customer a
left join ( select customer_id,sum(amount) amount from vmes_finance_bill
and company_id = #{companyId}
and period = #{period}
and type = '6' and state = '2'
group by customer_id ) b on a.id = b.customer_id
left join ( select customer_id,sum(amount) amount from vmes_finance_bill
and company_id = #{companyId}
and period = #{period}
and type in ('7','9') and state = '2'
group by customer_id ) c on a.id = c.customer_id
left join ( select customer_id,sum(amount) amount from vmes_finance_bill
and company_id = #{companyId}
and period = #{period}
and type = '1' and state = '2'
group by customer_id ) d on a.id = d.customer_id
and a.company_id = #{companyId}
and a.genre = '49c0a7ebcb4c4175bd5195837a6a9a13'
) nowBill on customer.id = nowBill.customer_id
and customer.company_id = #{companyId}
and customer.genre = '49c0a7ebcb4c4175bd5195837a6a9a13'
and (customer.code LIKE CONCAT(CONCAT('%', #{customerValue}),'%') or customer.name LIKE CONCAT(CONCAT('%', #{customerValue}),'%'))

select md5(uuid()) id, DATE_FORMAT(bill.cdate,'%Y-%m-%d') cdate, bill.code, bill.company_id companyId, bill.customer_id customerId, bill.period, bill.type, case when bill.type='1' then '付款单' when bill.type='6' then '收货账单' when bill.type='7' then '扣款单' when bill.type='9' then '退款单' else null end typeName,
bill.amount, case when bill.type in ('1') then bill.amount else null end paymentAmount,
case when bill.type in ('6') then bill.amount when bill.type in ('7','9') then bill.amount * -1 else null end occurAmount, 'bill' queryType
from vmes_finance_bill bill left join vmes_customer customer on bill.customer_id = customer.id
and bill.company_id = #{companyId}
and bill.customer_id = #{customerId}
and bill.period = #{period}
and customer.genre = '49c0a7ebcb4c4175bd5195837a6a9a13'
and bill.type in ('1','6','7','9')
and bill.state = '2'
union all
select md5(uuid()) id, DATE_FORMAT(history.cdate,'%Y-%m-%d') cdate, '' code, history.company_id companyId, history.customer_id customerId, history.period, '' type, '上期结转' typeName,
round(ifnull(begin_value,0),2) amount, '' paymentAmount, '' occurAmount, 'history' queryType from vmes_finance_history history left join vmes_customer customer on history.customer_id = customer.id
and history.company_id = #{companyId}
and history.customer_id = #{customerId}
and history.period = #{period}
and customer.genre = '49c0a7ebcb4c4175bd5195837a6a9a13'
