mapper.select:
select

from






                 and id <> #{id} 


                and id = #{id}







        and ${queryStr}



    order by ${orderStr}

select financeInvoice.code,customer.name,customerInvoice.company_name companyName,customerInvoice.invoice_number invoiceNumber, customerInvoice.bank_code bankCode,customerInvoice.bank_name bankName,customerInvoice.address, customerInvoice.phone, customerInvoice.mobile,cuser.user_name userName,financeInvoice.cdate, financeInvoice.amount,financeInvoice.state from vmes_finance_invoice financeInvoice left join vmes_customer customer on customer.id = financeInvoice.customer_id left join vmes_customer_invoice customerInvoice on customerInvoice.id = financeInvoice.customer_invoice_id left join vmes_user cuser on customerInvoice.cuser = cuser.id
and ${queryStr}
and code LIKE CONCAT(CONCAT('%', #{code}),'%')
and name LIKE CONCAT(CONCAT('%', #{name}),'%')
order by ${orderStr}
