mapper.select:
select prod.name productName, prod.spec spec, genre.name genreName, unit.name unitName, prod.id id, prod.company_id companyId, prod.qrcode qrcode, prod.photo photo, prod.code code, prod.genre genre,
'' prodProperty,
ROUND(prod.price, 2) price, prod.type type, prod.validity_days validityDays, prod.remark remark, prod.cuser cuser, DATE_FORMAT(prod.cdate,'%Y-%m-%d %T') cdate, prod.uuser uuser, DATE_FORMAT(prod.udate,'%Y-%m-%d %T') udate,
round(ifnull(prod.lock_count, 0), 2) lockCount, round(ifnull(prod.stock_count, 0), 2) stockCount, round((ifnull(prod.stock_count, 0) - ifnull(prod.lock_count, 0)), 2) productStockCount
from vmes_product prod left join vmes_product_unit produnit on produnit.product_id = prod.id and produnit.type = 1 left join vmes_dictionary unit on produnit.unit = unit.id left join vmes_dictionary genre on prod.genre = genre.id
and prod.company_id=#{companyId}
and prod.code=#{code}
and prod.name=#{name}
and prod.spec=#{spec}
and genre.name=#{genre}
and prod.spec <= #{max}
and prod.spec >= #{min}
order by ${orderStr}
