mapper.select:
select '1' as taskType, 'warehouseIn' taskTypeName, win.code code, detail.id taskDetailID, round(ifnull(detail.count,0), 2) taskCount, detail.product_id productID, product.code productCode, product.name productName, product.spec spec, genre.name genreName, unit.name unitName, executor.cdate cDate, executor.udate uDate from vmes_warehouse_in_executor executor left join vmes_warehouse_in_detail detail on executor.detail_id = detail.id left join vmes_warehouse_in win on win.id = detail.parent_id left join vmes_product product on detail.product_id = product .id left join vmes_product_unit produnit on produnit.product_id = product .id and produnit.type = 1 left join vmes_dictionary unit on produnit.unit = unit.id left join vmes_dictionary genre on product.genre = genre.id
and win.company_id=#{currentCompanyId}
and executor.executor_id=#{executorId}
and detail.state = #{dtlState}
union all
select '2' as taskType, 'warehouseOut' taskTypeName, wout.code code, detail.id taskDetailID, round(ifnull(detail.count,0), 2) taskCount, detail.product_id productID, product.code productCode, product.name productName, product.spec spec, genre.name genreName, unit.name unitName, executor.cdate cDate, executor.udate uDate from vmes_warehouse_out_executor executor left join vmes_warehouse_out_detail detail on executor.detail_id = detail.id left join vmes_warehouse_out wout on wout.id = detail.parent_id left join vmes_product product on detail.product_id = product .id left join vmes_product_unit produnit on produnit.product_id = product .id and produnit.type = 1 left join vmes_dictionary unit on produnit.unit = unit.id left join vmes_dictionary genre on product.genre = genre.id
and wout.company_id=#{currentCompanyId}
and executor.executor_id=#{executorId}
and detail.state = #{dtlState}
union all
select '3' as taskType, 'warehouseCheck' taskTypeName, wcheck.code code, detail.id taskDetailID, round(ifnull(detail.stock_count,0), 2) taskCount, detail.product_id productID, product.code productCode, product.name productName, product.spec spec, genre.name genreName, unit.name unitName, executor.cdate cDate, executor.udate uDate from vmes_warehouse_check_executor executor left join vmes_warehouse_check_detail detail on executor.detail_id = detail.id left join vmes_warehouse_check wcheck on wcheck.id = detail.parent_id left join vmes_product product on detail.product_id = product .id left join vmes_product_unit produnit on produnit.product_id = product .id and produnit.type = 1 left join vmes_dictionary unit on produnit.unit = unit.id left join vmes_dictionary genre on product.genre = genre.id
and wcheck.company_id=#{currentCompanyId}
and executor.executor_id=#{executorId}
and detail.state = #{dtlState}
union all
select '4' as taskType, 'warehouseMove' taskTypeName, wmove.code code, detail.id taskDetailID, round(ifnull(detail.count,0), 2) taskCount, detail.warehouse_product_id productID, product.code productCode, product.name productName, product.spec spec, genre.name genreName, unit.name unitName, executor.cdate cDate, executor.udate uDate
from vmes_warehouse_move_executor executor left join vmes_warehouse_move_detail detail on executor.detail_id = detail.id left join vmes_warehouse_product wp on wp.id = detail.warehouse_product_id left join vmes_warehouse_move wmove on wmove.id = detail.parent_id left join vmes_product product on wp.product_id = product .id left join vmes_product_unit produnit on produnit.product_id = product .id and produnit.type = 1 left join vmes_dictionary unit on produnit.unit = unit.id left join vmes_dictionary genre on product.genre = genre.id
and wmove.company_id=#{currentCompanyId}
and executor.executor_id=#{executorId}
and detail.state = #{dtlState}
order by taskType,cDate desc
