mapper.select:
select detail.id id, detail.code code, detail.parent_id parentId, detail.product_id productId, detail.unit_id unitId, round(ifnull(detail.count, 0), 2) count,
detail.begin_date beginDate, detail.end_date endDate, detail.plan_date planDate, detail.state state, detail.cuser cuser,
detail.cdate cdate, detail.uuser uuser, detail.udate udate, detail.remark remark, detail.isdisable isdisable,
CASE WHEN detail.state = '0' THEN '待生产' WHEN detail.state = '1' THEN '生产中' WHEN detail.state = '2' THEN '已完成' WHEN detail.state = '-1' THEN '已取消' ELSE null END stateName,
product.code sysProductCode, product.source_code custProductCode, CASE WHEN product.source_code is null THEN product.code WHEN product.source_code is not null THEN product.source_code ELSE null END productCode, product.name productName, product.spec productSpec, product.genre productGenre, product.property productProperty, unitName.name unitName,
'' fineCount, '' badCount
from vmes_produce_plan_detail detail left join vmes_product product on detail.product_id = product.id left join vmes_dictionary unitName on detail.unit_id = unitName.id
and detail.id in (${ids})
and detail.parent_id = #{parentId}
and detail.state = #{state}
and ${queryStr}
order by ${orderStr}
