mapper.delete:
delete from vmes_produce_plan
where id in

    #{item}

mapper.select:
select id id, company_id companyId, make_id makeId, sys_code sysCode, cust_code custCode,
produce_type produceType, begin_date beginDate, end_date endDate, plan_date planDate, state state,
cuser cuser, cdate cdate, uuser uuser, udate udate, remark remark,
isdisable isdisable from vmes_produce_plan
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select plan.id id, plan.company_id companyId, plan.make_id makeId, plan.sys_code sysCode, plan.cust_code custCode,
plan.produce_type produceType, DATE_FORMAT(plan.begin_date,'%Y-%m-%d') beginDate, DATE_FORMAT(plan.end_date,'%Y-%m-%d') endDate, plan.plan_date planDate, plan.state state,
plan.cuser cuser, DATE_FORMAT(plan.cdate,'%Y-%m-%d %T') cdate, plan.uuser uuser, plan.udate udate, plan.remark remark,
plan.isdisable isdisable,
CASE WHEN plan.state = '0' THEN '待生产' WHEN plan.state = '1' THEN '生产中' WHEN plan.state = '2' THEN '已完成' WHEN plan.state = '-1' THEN '已取消' ELSE null END planStateName, produceType.name produceTypeName, makeUser.user_name makeName
from vmes_produce_plan plan left join vmes_dictionary produceType on plan.produce_type = produceType.id left join vmes_user makeUser on plan.make_id = makeUser.id
and plan.id = #{planId}
and plan.company_id = #{currentCompanyId}
and plan.sys_code = #{sysCode}
and plan.state = #{planState}
and plan.cdate >= #{beginDate}
and plan.cdate <= #{endDate}
and ${queryStr}
order by ${orderStr}
