mapper.delete:
delete from vmes_purchase_payment_history
where id in

    #{item}

mapper.select:
select id id, supplier_id supplierId, payment_period paymentPeriod, begin_plus beginPlus, begin_minus beginMinus,
begin_value beginValue, now_order_sum nowOrderSum, now_payment_sum nowPaymentSum, end_plus endPlus, end_minus endMinus,
end_value endValue, cuser cuser, cdate cdate, uuser uuser, udate udate,
remark remark, isdisable isdisable from vmes_purchase_payment_history
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select a.id id, a.supplier_id supplierId, b.code supplierCode, b.name supplierName,
a.begin_plus beginPlus, a.begin_minus beginMinus, a.begin_value beginValue,
a.now_order_sum nowOrderSum, a.now_payment_sum nowPaymentSum,
a.end_value endValue, a.end_plus endPlus, a.end_minus endMinus,
a.payment_period paymentPeriod from vmes_purchase_payment_history a left join vmes_customer b on a.supplier_id = b.id
and b.company_id = #{currentCompanyId}
and a.payment_period < #{dateByNow}
UNION ALL select replace(uuid(), '-', '') id, supplier.id supplierId, supplier.code supplierCode, supplier.name supplierName,
round(ifnull(paymentHist.end_plus, 0),2) beginPlus, round(ifnull(paymentHist.end_minus, 0),2) beginMinus, round(ifnull(paymentHist.end_value, 0),2) beginValue,
round(ifnull(purchaseOrder.purchaseSum, 0),2) nowOrderSum, round(ifnull(purchasePayment.paymentSum, 0),2) nowReceiveSum,
round((ifnull(purchaseOrder.purchaseSum, 0) - ifnull(purchasePayment.paymentSum, 0) + ifnull(paymentHist.end_value, 0)), 2) endValue, '' endPlus, '' endMinus
,#{dateByNow} paymentPeriod
from ( select a.id,a.code,a.name from vmes_customer a left join vmes_purchase_payment_build b on a.id = b.supplier_id
and a.company_id = #{currentCompanyId} and a.genre = '49c0a7ebcb4c4175bd5195837a6a9a13'
and b.payment_period >= #{dateByBefore}
) supplier left join ( select supplier_id,payment_period,end_plus,end_minus,end_value from vmes_purchase_payment_history
and payment_period = #{dateByBefore}
) paymentHist on supplier.id = paymentHist.supplier_id
left join ( select supplier_id, sum(amount) purchaseSum from vmes_purchase_order
and company_id = #{currentCompanyId}
and date_format(cdate, '%Y%m') = #{dateByNow}
group by supplier_id ) purchaseOrder on supplier.id = purchaseOrder.supplier_id
left join ( select supplier_id,sum(payment_sum) paymentSum from vmes_purchase_payment_record
and company_id = #{currentCompanyId} and type = '1'
and date_format(cdate, '%Y%m') = #{dateByNow}
group by supplier_id ) purchasePayment on supplier.id = purchasePayment.supplier_id
