mapper.select:
select

from






                 and id <> #{id} 


                and id = #{id}







        and ${queryStr}



    order by ${orderStr}

select
from vmes_purchase_plan plan left join vmes_purchase_plan_detail detail on plan.id = detail.parent_id left join vmes_product product on product.id = detail.product_id left join vmes_user make on make.id = plan.make_id
and ${queryStr}
and plan.company_id LIKE CONCAT(CONCAT('%', #{currentCompanyId}),'%')
and plan.state = #{state}
and plan.code LIKE CONCAT(CONCAT('%', #{code}),'%')
and product.name LIKE CONCAT(CONCAT('%', #{productName}),'%')
and product.code LIKE CONCAT(CONCAT('%', #{productCode}),'%')
and make.user_name LIKE CONCAT(CONCAT('%', #{makeName}),'%')
order by ${orderStr}

