mapper.delete:
        delete from vmes_purchase_quality_detail
        where id in

		#{item}

mapper.select:
select id id, sign_detail_id signDetailId, product_id productId, order_unit orderUnit, quality_id qualityId,
bad_count badCount, cuser cuser, cdate cdate, uuser uuser, udate udate,
remark remark, isdisable isdisable from vmes_purchase_quality_detail
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select detail.id id, detail.sign_detail_id signDetailId, detail.product_id productId, detail.order_unit orderUnit, detail.quality_id qualityId,
round(ifnull(detail.bad_count, 0), 2) badCount, detail.cuser cuser, detail.cdate cdate, detail.uuser uuser, detail.udate udate,
detail.remark remark, detail.isdisable isdisable,
quality.name qualityName, quality.criterion qualityCriterion
from vmes_purchase_quality_detail detail left join vmes_quality quality on detail.quality_id = quality.id
and detail.sign_detail_id = #{signDetailId}
and ${queryStr}
order by ${orderStr}
