mapper.update:
update vmes_purchase_retreat_detail set
state = #{state},
udate = now()


        and parent_id = #{parentId}


        and ${parentIds}

mapper.select:
select id id, parent_id parentId, order_detail_id orderDetailId, out_detail_id outDetailId, product_id productId,
amount amount, unit unit, count count, state state, cuser cuser,
cdate cdate, uuser uuser, udate udate, remark remark, isdisable isdisable from vmes_purchase_retreat_detail
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select detail.id id, detail.parent_id parentId, detail.order_detail_id orderDetailId, detail.out_detail_id outDetailId, detail.product_id productId,
round(ifnull(orderDetail.price, 0), 2) price, round(ifnull(detail.amount, 0), 2) amount, detail.unit unit, orderUnit.name unitName, round(ifnull(detail.count, 0), 2) count, detail.state state, detail.cuser cuser,
detail.cdate cdate, detail.uuser uuser, detail.udate udate, detail.remark remark, detail.isdisable isdisable,
product.code productCode, product.name productName, product.spec productSpec, product.genre productGenre, genre.name productGenreName,
prodUnit.np_formula n2pFormula, prodUnit.pn_formula p2nFormula
from vmes_purchase_retreat_detail detail left join vmes_purchase_order_detail orderDetail on detail.order_detail_id = orderDetail.id left join vmes_product product on detail.product_id = product.id left join vmes_dictionary orderUnit on detail.unit = orderUnit.id left join vmes_dictionary genre on product.genre = genre.id left join vmes_product_unit prodUnit on (detail.product_id = prodUnit.product_id and detail.unit = prodUnit.unit)
and detail.parent_id = #{parentId}
and detail.state = #{state}
and ${queryStr}
order by ${orderStr}
