select id id, order_id orderId, company_id companyId, sign_id signId, sdate sdate,
cuser cuser, cdate cdate, uuser uuser, udate udate, remark remark,
isdisable isdisable, sys_code sysCode, cust_code custCode, state state from vmes_purchase_sign sign left join vmes_user vmesuser on vmesuser.id = sign.sign_id
and sign.order_id = #{orderId}
and ${queryStr}
order by ${orderStr}
