mapper.delete:
        delete from vmes_purchase_sign
        where id in

		#{item}

mapper.select:
select id id, order_id orderId, company_id companyId, sign_id signId, sdate sdate,
cuser cuser, cdate cdate, uuser uuser, udate udate, remark remark,
isdisable isdisable, sys_code sysCode, cust_code custCode, state state from vmes_purchase_sign
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select distinct sign.id id, sign.order_id orderId, sign.company_id companyId, sign.sign_id signId, DATE_FORMAT(sign.sdate,'%Y-%m-%d') sdate,
sign.cuser cuser, DATE_FORMAT(sign.cdate,'%Y-%m-%d') cdate, sign.uuser uuser, sign.udate udate, sign.remark remark,
sign.isdisable isdisable, sign.sys_code sysCode, sign.cust_code custCode, sign.state state,
CASE WHEN sign.state = '1' THEN '检验中' WHEN sign.state = '2' THEN '已完成' WHEN sign.state = '-1' THEN '已取消' ELSE null END stateName,
purchaseOrder.code orderCode, purchaseOrder.supplier_id supplierId, supplier.name supplierName, signUser.user_name signUserName,
'' isShowEdit
from vmes_purchase_sign sign left join vmes_purchase_order purchaseOrder on sign.order_id = purchaseOrder.id left join vmes_customer supplier on purchaseOrder.supplier_id = supplier.id left join vmes_user signUser on sign.sign_id = signUser.id
left join vmes_purchase_sign_detail detail on sign.id = detail.parent_id
and sign.id = #{signId}
and sign.company_id = #{currentCompanyId}
and sign.order_id = #{orderId}
and sign.sys_code LIKE CONCAT(CONCAT('%', #{sysCode}),'%')
and sign.state = #{signState}
and detail.quality = #{quality}
and purchaseOrder.code LIKE CONCAT(CONCAT('%', #{orderCode}),'%')
and supplier.name LIKE CONCAT(CONCAT('%', #{supplierName}),'%')
and ${queryStr}
order by ${orderStr}
