mapper.select:
select
COUNT(saleOrder.id) allNum,
SUM(
    CASE
        WHEN saleOrder.state = '2' THEN 1
        WHEN saleOrder.state = '3' THEN 0
    ELSE 0
    END
) undoNum
from vmes_sale_order saleOrder



        and saleOrder.company_id = #{currentCompanyId}


          and saleOrder.cdate > #{startDate} 


          and saleOrder.cdate < #{endDate} 

    and saleOrder.state > '1'


        and ${queryStr}




    order by ${orderStr}

select
round(sum(ifnull(order_count,0)),2) number,
round(sum(ifnull(product_price*order_count,0)),2) volume
from vmes_sale_order saleOrder
left join vmes_sale_order_detail saleOrderDetail on saleOrderDetail.parent_id = saleOrder.id



        and saleOrder.company_id = #{currentCompanyId}


          and saleOrder.cdate > #{startDate} 


          and saleOrder.cdate < #{endDate} 

    and saleOrder.state > '1'
    and saleOrderDetail.state > '1'


        and ${queryStr}




    order by ${orderStr}

select
DATE_FORMAT(saleOrderDetail.cdate,'%Y-%m') month,
round(sum(ifnull(saleOrderDetail.order_count,0)),2) number,
round(sum(ifnull(saleOrderDetail.product_price*saleOrderDetail.order_count,0)),2) volume
from vmes_sale_order saleOrder
left join vmes_sale_order_detail saleOrderDetail on saleOrderDetail.parent_id = saleOrder.id



        and saleOrder.company_id = #{currentCompanyId}


          and saleOrder.cdate > #{startDate} 


          and saleOrder.cdate < #{endDate} 

    and saleOrder.state > '1'
    and saleOrderDetail.state > '1'


        and ${queryStr}



group by DATE_FORMAT(saleOrderDetail.cdate,'%Y-%m')


    order by ${orderStr}

select
customer.id id,
customer.name name,
round(sum(ifnull(saleOrderDetail.product_price*saleOrderDetail.order_count,0)),2) volume
from vmes_sale_order saleOrder
left join vmes_sale_order_detail saleOrderDetail on saleOrderDetail.parent_id = saleOrder.id
left join vmes_customer customer on saleOrder.customer_id = customer.id


        and saleOrder.company_id = #{currentCompanyId}


          and saleOrder.cdate > #{startDate} 


          and saleOrder.cdate < #{endDate} 

    and saleOrder.state > '1'
    and saleOrderDetail.state > '1'
    and customer.id is not null


        and ${queryStr}


group by customer.id


    order by ${orderStr}

select product.id id, concat(product.name,'/',product.spec) name, round(sum(ifnull(saleOrderDetail.order_count,0)),2) number, round(sum(ifnull(saleOrderDetail.product_price*saleOrderDetail.order_count,0)),2) volume from vmes_sale_order saleOrder left join vmes_sale_order_detail saleOrderDetail on saleOrderDetail.parent_id = saleOrder.id left join vmes_product product on saleOrderDetail.product_id = product.id
and saleOrder.company_id = #{currentCompanyId}
and saleOrder.cdate > #{startDate}
and saleOrder.cdate < #{endDate}
and saleOrder.state > '1' and saleOrderDetail.state > '1' and product.id is not null
and ${queryStr}
group by product.id
order by ${orderStr}

select
product.id id,
concat(product.name,'/',product.spec) name,
round(sum(ifnull(retreatDetail.order_count,0)),2) number
from vmes_sale_retreat retreat
left join vmes_sale_retreat_detail retreatDetail on retreatDetail.parent_id = retreat.id
left join vmes_product product on retreatDetail.product_id = product.id


        and retreat.company_id = #{currentCompanyId}


          and retreat.cdate > #{startDate} 


          and retreat.cdate < #{endDate} 

    and retreat.state > '1'
    and retreatDetail.state > '1'
    and product.id is not null


        and ${queryStr}


group by product.id


    order by ${orderStr}

select makeUser.id id, count(distinct saleOrder.id) orderNumber, makeUser.user_name name, round(sum(ifnull(saleOrderDetail.order_count,0)),2) salesNumber, round(sum(ifnull(saleOrderDetail.product_price*saleOrderDetail.order_count,0)),2) salesVolume from vmes_sale_order saleOrder left join vmes_sale_order_detail saleOrderDetail on saleOrderDetail.parent_id = saleOrder.id left join vmes_user makeUser on saleOrder.make_id = makeUser.id
and saleOrder.company_id = #{currentCompanyId}
and saleOrder.cdate > #{startDate}
and saleOrder.cdate < #{endDate}
and saleOrder.state > '1' and saleOrderDetail.state > '1' and makeUser.id is not null
and ${queryStr}
group by makeUser.id
order by ${orderStr}

select makeUser.id id, DATE_FORMAT(saleOrderDetail.cdate,'%Y-%m') month, makeUser.user_name name, round(sum(ifnull(saleOrderDetail.product_price*saleOrderDetail.order_count,0)),2) salesVolume from vmes_sale_order saleOrder left join vmes_sale_order_detail saleOrderDetail on saleOrderDetail.parent_id = saleOrder.id left join vmes_user makeUser on saleOrder.make_id = makeUser.id
and saleOrder.company_id = #{currentCompanyId}
and saleOrder.cdate > #{startDate}
and saleOrder.cdate < #{endDate}
and saleOrder.state > '1' and saleOrderDetail.state > '1' and makeUser.id is not null
and ${queryStr}
group by makeUser.id,DATE_FORMAT(saleOrderDetail.cdate,'%Y-%m')
order by ${orderStr}
