mapper.select:
select deliverDtl.order_detai_id orderDetailId, round(ifnull(orderDtl.order_count, 0), 2) orderCount, round(ifnull(orderDtl.need_deliver_count, 0), 2) needDeliverCount, round(ifnull(deliverOrderDtl.orderCount, 0), 2) orderDtlDeliverCount, round(ifnull(deliverOrderDtl.orderSum, 0), 2) orderDtlDeliverSum from ( select order_detai_id from vmes_sale_deliver_detail
and parent_id = #{deliverId}
group by order_detai_id ) deliverDtl left join ( select order_detai_id, sum(order_count) orderCount, sum(sum) orderSum from vmes_sale_deliver_detail
and order_detai_id = #{orderDtlId}
and order_detai_id in (${orderDtlIds})
and state = #{deliveDtlState}
group by order_detai_id ) deliverOrderDtl on deliverDtl.order_detai_id = deliverOrderDtl.order_detai_id left join vmes_sale_order_detail orderDtl on deliverDtl.order_detai_id = orderDtl.id
