mapper.select:
select outDetail.id outDtlId, outDetail.parent_id outId, outDetail.product_id productId, outDetail.state outDetailState, round(ifnull(outDetail.count, 0), 2) outDetailCount,
deliverDetail.id deliverDtlId, deliverDetail.parent_id deliverId,
orderDetail.id orderDtlId, orderDetail.parent_id orderId, orderDetail.lock_count lockCount from vmes_sale_deliver_detail deliverDetail left join vmes_warehouse_out_detail outDetail on deliverDetail.out_detail_id = outDetail.id left join vmes_sale_order_detail orderDetail on deliverDetail.order_detai_id = orderDetail.id
and deliverDetail.order_id = #{orderId}
and deliverDetail.state = #{deliverDtlState}
and outDetail.id = #{outDtlId}
and outDetail.state = #{outDetailState}
