mapper.delete:
delete from vmes_sale_invoice
where id in

    #{item}

mapper.select:
select id id, company_id companyId, customer_id customerId, order_id orderId, receipt_type receiptType,
sys_code sysCode, invoice_number invoiceNumber, total_sum totalSum, state state, cuser cuser,
cdate cdate, uuser uuser, udate udate, remark remark, isdisable isdisable from vmes_sale_invoice
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select invoice.id id, invoice.id parentId, invoice.company_id companyId, invoice.customer_id customerId, invoice.order_id orderId, invoice.receipt_type receiptType, CASE WHEN saleOrder.receipt_type = '1' THEN '不开票' WHEN saleOrder.receipt_type = '2' THEN '普通发票' WHEN saleOrder.receipt_type = '3' THEN '增值税发票' ELSE null END receiptTypeName,
invoice.sys_code sysCode, invoice.invoice_number invoiceNumber, round(ifnull(invoice.total_sum, 0),2) totalSum, invoice.state state, CASE WHEN invoice.state = '0' THEN '待开票' WHEN invoice.state = '1' THEN '已开票' WHEN invoice.state = '-1' THEN '已取消' ELSE null END stateName, invoice.cuser cuser, cuser.user_name cuserName, cuser.dept_id cuserDeptId, cuserDept.name cuserDeptName,
DATE_FORMAT(invoice.cdate,'%Y-%m-%d %T') cdate, invoice.uuser uuser, DATE_FORMAT(invoice.udate,'%Y-%m-%d %T') udate, invoice.remark remark, invoice.isdisable isdisable,
saleOrder.sys_code orderSysCode, saleOrder.order_number orderNumber, customer.name customerName,
customerInvoice.company_name custInvoiceCompanyName, customerInvoice.invoice_number custInvoiceNumber, customerInvoice.address custInvoiceAddress, customerInvoice.bank_name custInvoiceBankName, customerInvoice.bank_code custInvoiceBankCode, customerInvoice.phone custInvoicePhone, customerInvoice.mobile custInvoiceMobile
from vmes_sale_invoice invoice left join vmes_user cuser on invoice.cuser = cuser.id left join vmes_department cuserDept on cuser.dept_id = cuserDept.id left join vmes_sale_order saleOrder on invoice.order_id = saleOrder.id left join vmes_customer customer on invoice.customer_id = customer.id left join vmes_customer_invoice customerInvoice on invoice.customer_id = customerInvoice.customer_id and customerInvoice.isdefault = '1'
and invoice.company_id = #{currentCompanyId}
and invoice.sys_code = #{sysCode}
and invoice.invoice_number = #{orderNumber}
and invoice.state = #{state}
and saleOrder.sys_code = #{orderSysCode}
and saleOrder.order_number = #{orderNumber}
and customer.name LIKE CONCAT(CONCAT(#{customerName}),'%')
and ${queryStr}
order by ${orderStr}
