mapper.select:
select distinct saleOrder.id id, saleOrder.id parentId, saleOrder.company_id companyId, saleOrder.order_number orderNumber, saleOrder.sys_code sysCode, saleOrder.receipt_type receiptType, CASE WHEN saleOrder.receipt_type = '1' THEN '不开票' WHEN saleOrder.receipt_type = '2' THEN '增值税普通发票' WHEN saleOrder.receipt_type = '3' THEN '增值税专用发票' ELSE null END receiptTypeName,
saleOrder.price_type priceType, saleOrder.state state, saleOrder.state orderStateByQuery, CASE WHEN saleOrder.state = '0' THEN '待提交' WHEN saleOrder.state = '1' THEN '待审核' WHEN saleOrder.state = '2' THEN '待出库' WHEN saleOrder.state = '3' THEN '待发货' WHEN saleOrder.state = '4' THEN '已发货' WHEN saleOrder.state = '5' THEN '已完成' WHEN saleOrder.state = '-1' THEN '已取消' ELSE null END stateName, saleOrder.make_id makeId, saleOrder.customer_id customerId, DATE_FORMAT(saleOrder.order_date,'%Y-%m-%d') orderDate,
round(ifnull(saleOrder.total_sum, 0),2) totalSum, round(ifnull(saleOrder.discount_sum, 0),2) discountSum, round(ifnull(saleOrder.order_sum, 0),2) orderSum, round(ifnull(saleOrder.advance_sum, 0),2) advanceSum,
saleOrder.cuser cuser, DATE_FORMAT(saleOrder.cdate,'%Y-%m-%d %T') cdate, saleOrder.uuser uuser, DATE_FORMAT(saleOrder.udate,'%Y-%m-%d %T') udate, saleOrder.remark remark,
saleOrder.isdisable isdisable, DATE_FORMAT(saleOrder.expect_date,'%Y-%m-%d') expectDate, DATE_FORMAT(saleOrder.deliver_date,'%Y-%m-%d') deliverDate, DATE_FORMAT(saleOrder.pay_date,'%Y-%m-%d') payDate,
makeUser.user_name makeName, customer.name customerName,
round(ifnull(receive.amount, 0),2) receiveSum,
CASE WHEN ifnull(saleOrder.order_sum, 0) = 0 THEN 0 WHEN ifnull(saleOrder.order_sum, 0) <> 0 THEN round((ifnull(receive.amount, 0) / ifnull(saleOrder.order_sum, 0)) * 100, 0) ELSE null END receiveProgress,
round(ifnull(deliverDtl.deliverSum, 0), 2) deliverSum, round((ifnull(deliverDtl.deliverSum, 0) - ifnull(saleOrder.advance_sum, 0)), 2) receivables,
invoiceOrderTable.isInvoice
from vmes_sale_order saleOrder left join vmes_customer customer on saleOrder.customer_id = customer.id left join vmes_user makeUser on saleOrder.make_id = makeUser.id left join vmes_sale_order_detail orderDetail on saleOrder.id = orderDetail.parent_id
left join view_sale_receive_detail receive on saleOrder.id = receive.order_id
left join ( select order_id, sum(sum) deliverSum from vmes_sale_deliver_detail where state = '1' group by order_id ) deliverDtl on saleOrder.id = deliverDtl.order_id
left join ( select orderCount.orderId,
round(ifnull(orderCount.order_count, 0),2) orderCount,
round(ifnull(invoiceOrder.order_count, 0),2) invoiceCount, CASE
WHEN ifnull(invoiceOrder.order_count, 0) = 0 THEN '未开票'
WHEN ifnull(invoiceOrder.order_count, 0) > 0 and (ifnull(invoiceOrder.order_count, 0) < ifnull(orderCount.order_count, 0)) THEN '开票中'
WHEN ifnull(invoiceOrder.order_count, 0) >= ifnull(orderCount.order_count, 0) THEN '开票完成' ELSE null END isInvoice from (
select parent_id orderId, sum(order_count) order_count from vmes_sale_order_detail where parent_id in ( select id from vmes_sale_order
and company_id = #{currentCompanyId}
and state <> '-1'
) group by parent_id ) orderCount left join (
select orderDtl.parent_id orderId, sum(invoice.count) order_count from ( select order_detail_id,count from vmes_sale_invoice_detail where parent_id in ( select id from vmes_sale_invoice
and company_id = #{currentCompanyId}
) and state <> '-1' ) invoice left join vmes_sale_order_detail orderDtl on invoice.order_detail_id = orderDtl.id group by orderDtl.parent_id ) invoiceOrder on orderCount.orderId = invoiceOrder.orderId ) invoiceOrderTable on saleOrder.id = invoiceOrderTable.orderId
and saleOrder.company_id = #{currentCompanyId}
and saleOrder.state = #{state}
and orderDetail.state = #{orderDtlState}
and saleOrder.sys_code = #{sysCode}
and saleOrder.order_number = #{orderNumber}
and saleOrder.price_type = #{priceType}
and customer.name LIKE CONCAT(CONCAT(#{customerName}),'%')
and saleOrder.receipt_type = #{receiptType}
and saleOrder.order_date >= #{orderDateBegin}
and saleOrder.order_date <= #{orderDateEnd}
and (detail.is_lock_warehouse = '1' and detail.state = '3')
and ${queryStr}
order by ${orderStr}
