mapper.delete:
        delete from vmes_sale_order_change
        where id in

		#{item}

mapper.select:
select
    id id,
    company_id companyId,
    order_id orderId,
    audit_id auditId,
    state state,

    receipt_type_before receiptTypeBefore,
    receipt_type_after receiptTypeAfter,
    cuser cuser,
    cdate cdate,
    uuser uuser,

    udate udate,
    remark remark,
    isdisable isdisable
from vmes_sale_order_change





                 and id <> #{id} 


                and id = #{id}





        and company_id = #{companyId}


        and order_id = #{orderId}


        and state = #{state}




        and ${queryStr}



    order by ${orderStr}

select ordeChange.id id, ordeChange.company_id companyId, ordeChange.order_id orderId, ordeChange.audit_id auditId, ordeChange.state state,
CASE WHEN ordeChange.state = '0' THEN '审核中' WHEN ordeChange.state = '1' THEN '审核通过' WHEN ordeChange.state = '2' THEN '审核不通过' ELSE null END changeStateName,
ordeChange.receipt_type_before receiptTypeBefore, CASE WHEN ordeChange.receipt_type_before = '1' THEN '不开票' WHEN ordeChange.receipt_type_before = '2' THEN '增值税普通发票' WHEN ordeChange.receipt_type_before = '3' THEN '增值税专用发票' ELSE null END beforeReceiptTypeName,
ordeChange.receipt_type_after receiptTypeAfter, CASE WHEN ordeChange.receipt_type_after = '1' THEN '不开票' WHEN ordeChange.receipt_type_after = '2' THEN '增值税普通发票' WHEN ordeChange.receipt_type_after = '3' THEN '增值税专用发票' ELSE null END afterReceiptTypeName, ordeChange.cuser cuser, ordeChange.cdate cdate, ordeChange.uuser uuser,
ordeChange.udate udate, ordeChange.remark remark, ordeChange.isdisable isdisable,
saleOrder.order_number orderNumber, saleOrder.sys_code sysCode, DATE_FORMAT(saleOrder.order_date,'%Y-%m-%d') orderDate, makeUser.user_name makeName, customer.name customerName from vmes_sale_order_change ordeChange left join vmes_sale_order saleOrder on ordeChange.order_id = saleOrder.id left join vmes_customer customer on saleOrder.customer_id = customer.id left join vmes_user makeUser on saleOrder.make_id = makeUser.id
and ordeChange.company_id = #{companyId}
and ordeChange.company_id = #{currentCompanyId}
and ordeChange.order_id = #{orderId}
and ordeChange.state = #{state}
and saleOrder.sys_code = #{sysCode}
and saleOrder.order_number = #{orderNumber}
and customer.name LIKE CONCAT(CONCAT(#{customerName}),'%')
and saleOrder.order_date >= #{orderDateBegin}
and saleOrder.order_date <= #{orderDateEnd}
and ${queryStr}
order by ${orderStr}
