mapper.select:
select saleOrder.id id, saleOrder.company_id companyId, saleOrder.sys_code sysCode, saleOrder.receipt_type receiptType, CASE WHEN saleOrder.receipt_type = '1' THEN '不开票' WHEN saleOrder.receipt_type = '2' THEN '增值税普通发票' WHEN saleOrder.receipt_type = '3' THEN '增值税专用发票' ELSE null END receiptTypeName, saleOrder.price_type priceType, CASE WHEN saleOrder.price_type = '1' THEN '先计价' WHEN saleOrder.price_type = '2' THEN '后计价' ELSE null END priceTypeName,
saleOrder.state state, CASE WHEN saleOrder.state = '0' THEN '待提交' WHEN saleOrder.state = '1' THEN '待审核' WHEN saleOrder.state = '2' THEN '待发货' WHEN saleOrder.state = '3' THEN '已发货' WHEN saleOrder.state = '4' THEN '已完成' WHEN saleOrder.state = '-1' THEN '已取消' ELSE null END stateName, saleOrder.make_id makeId, saleOrder.audit_id auditId, saleOrder.customer_id customerId, saleOrder.remark remark, round(ifnull(saleOrder.advance_sum, 0),2) advanceAmount, round(ifnull(saleOrder.order_sum, 0),2) orderAmount, DATE_FORMAT(saleOrder.order_date,'%Y-%m-%d') orderDate, DATE_FORMAT(saleOrder.expect_date,'%Y-%m-%d') expectDate, DATE_FORMAT(saleOrder.deliver_date,'%Y-%m-%d') deliverDate, DATE_FORMAT(saleOrder.pay_date,'%Y-%m-%d') payDate, DATE_FORMAT(saleOrder.current_deliver_date,'%Y-%m-%d') currentDeliverDate, DATE_FORMAT(date_add(saleOrder.current_deliver_date, interval customer.account_days day),'%Y-%m-%d') deadlineDate, DATEDIFF(DATE_FORMAT(date_add(saleOrder.current_deliver_date, interval customer.account_days day),'%Y-%m-%d'),DATE_FORMAT(now(),'%Y-%m-%d')) daysRemaining, makeUser.user_name makeName, auditUser.user_name auditName, customer.name customerName, customer.account_days accountDays, round(ifnull(tab.deliverAmount, 0),2) deliverAmount, round(ifnull(tab.receivedAmount, 0),2) receivedAmount, round(ifnull(tab.discountedAmount, 0),2) discountedAmount, round(ifnull(tab.invoiceAmount, 0),2) invoiceAmount, round(ifnull(tab.retreatAmount, 0),2) retreatAmount, round(ifnull(tab.nowReceiveAmount, 0),2) nowReceiveAmount, round(ifnull(tab.preReceiveAmount, 0),2) preReceiveAmount from vmes_sale_order saleOrder left join ( select id, round(sum(ifnull(deliverAmount,0)),2) deliverAmount,round(sum(ifnull(receiveAmount,0)),2) receivedAmount,round(sum(ifnull(discountAmount,0)),2) discountedAmount,round(sum(ifnull(invoiceAmount,0)),2) invoiceAmount,round(sum(ifnull(retreatAmount,0)),2) retreatAmount, case when sum(ifnull(deliverAmount,0))-sum(ifnull(retreatAmount,0)) > (sum(ifnull(receiveAmount,0))+sum(ifnull(discountAmount,0))) then round(sum(ifnull(deliverAmount,0)) - sum(ifnull(retreatAmount,0)) - sum(ifnull(receiveAmount,0)) - sum(ifnull(discountAmount,0)),2) else 0.00 end nowReceiveAmount, case when sum(ifnull(deliverAmount,0))-sum(ifnull(retreatAmount,0)) < (sum(ifnull(receiveAmount,0))+sum(ifnull(discountAmount,0))) then round(sum(ifnull(receiveAmount,0)) + sum(ifnull(discountAmount,0)) - sum(ifnull(deliverAmount,0)) + sum(ifnull(retreatAmount,0)),2) else 0.00 end preReceiveAmount from ( select sOrder.id id, deliverDetail.sum deliverAmount,null receiveAmount,null discountAmount,null invoiceAmount,null retreatAmount from vmes_sale_order sOrder left join vmes_sale_deliver_detail deliverDetail on deliverDetail.order_id = sOrder.id
and sOrder.id = #{id}
and deliverDetail.state = '1'
union all
select sOrder.id id, null deliverAmount,receiveDetail.receive_amount receiveAmount,receiveDetail.discount_amount discountAmount,null invoiceAmount,null retreatAmount from vmes_sale_order sOrder left join vmes_sale_receive_detail receiveDetail on receiveDetail.order_id = sOrder.id
and sOrder.id = #{id}
and receiveDetail.state = '1'
union all
select sOrder.id id, null deliverAmount,null receiveAmount,null discountAmount,null invoiceAmount,ifnull(retreatDetail.order_count,0)*ifnull(orderDtail.product_price,0) retreatAmount from vmes_sale_order sOrder left join vmes_sale_order_detail orderDtail on sOrder.id = orderDtail.parent_id left join vmes_sale_retreat_detail retreatDetail on orderDtail.id = retreatDetail.order_detail_id left join vmes_sale_retreat retreat on retreat.id = retreatDetail.parent_id
and sOrder.id = #{id}
and retreat.type = 'f69839bbf2394846a65894f0da120df9' and retreatDetail.state = '3'
union all
select sOrder.id id, null deliverAmount,null receiveAmount,null discountAmount,invoiceDetail.sum invoiceAmount,null retreatAmount from vmes_sale_order sOrder left join vmes_sale_order_detail orderDetail on orderDetail.parent_id = sOrder.id left join vmes_sale_invoice_detail invoiceDetail on invoiceDetail.order_detail_id = orderDetail.id
and sOrder.id = #{id}
and invoiceDetail.state = '1'
) t group by t.id )tab on saleOrder.id = tab.id left join vmes_customer customer on customer.id = saleOrder.customer_id left join vmes_user makeUser on saleOrder.make_id = makeUser.id left join vmes_user auditUser on saleOrder.audit_id = auditUser.id
and saleOrder.id = #{id}
and saleOrder.company_id = #{currentCompanyId}
order by ${orderStr}
