mapper.delete:
        delete from vmes_sale_order_detail_change
        where id in

		#{item}

mapper.select:
select id id, parent_id parentId, order_dtl_id orderDtlId, order_count_before orderCountBefore, order_count_after orderCountAfter,
product_price_before productPriceBefore, product_price_after productPriceAfter, deliver_date_before deliverDateBefore, deliver_date_after deliverDateAfter, deliver_count deliverCount,
retreat_count retreatCount, cuser cuser, cdate cdate, uuser uuser, udate udate,
remark remark, isdisable isdisable from vmes_sale_order_detail_change
and id <> #{id}
and id = #{id}
and parent_id = #{parentId}
and order_dtl_id = #{orderDtlId}
and ${queryStr}
order by ${orderStr}

select dtlChange.id id, dtlChange.parent_id parentId, dtlChange.order_dtl_id orderDtlId, round(ifnull(dtlChange.order_count_before, 0), 2) orderCountBefore, round(ifnull(dtlChange.order_count_after, 0), 2) orderCountAfter,
round(ifnull(dtlChange.product_price_before, 0), ) productPriceBefore, round(ifnull(dtlChange.product_price_after, 0), ) productPriceAfter, DATE_FORMAT(dtlChange.deliver_date_before,'%Y-%m-%d') deliverDateBefore, DATE_FORMAT(dtlChange.deliver_date_after,'%Y-%m-%d') deliverDateAfter, round(ifnull(dtlChange.deliver_count, 0), 2) deliverCount,
round(ifnull(dtlChange.retreat_count, 0), 2) retreatCount, dtlChange.cuser cuser, dtlChange.cdate cdate, dtlChange.uuser uuser, dtlChange.udate udate,
dtlChange.remark remark, dtlChange.isdisable isdisable,
round((ifnull(dtlChange.order_count_before, 0) * ifnull(dtlChange.product_price_before, 0)), 2) priceSumBefore, round((ifnull(dtlChange.order_count_after, 0) * ifnull(dtlChange.product_price_after, 0)), 2) priceSumAfter,
product.code sysProductCode, product.source_code custProductCode, CASE WHEN product.source_code is null THEN product.code WHEN product.source_code is not null THEN product.source_code ELSE null END productCode, product.name productName, product.spec productSpec, product.genre productGenre, genre.name productGenreName,
orderDtl.order_unit orderUnit, orderUnit.name orderUnitName, orderProdUnit.np_formula n2pFormula, orderProdUnit.pn_formula p2nFormula,
round(ifnull(orderDtl.order_count, 0),2) orderCount
from vmes_sale_order_detail_change dtlChange left join vmes_sale_order_change ordeChange on dtlChange.parent_id = ordeChange.id left join vmes_sale_order_detail orderDtl on dtlChange.order_dtl_id = orderDtl.id left join vmes_product product on orderDtl.product_id = product.id left join vmes_dictionary genre on product.genre = genre.id left join vmes_dictionary orderUnit on orderDtl.order_unit = orderUnit.id left join vmes_product_unit orderProdUnit on (orderDtl.product_id = orderProdUnit.product_id and orderDtl.order_unit = orderProdUnit.unit)
and dtlChange.parent_id = #{parentId}
and ordeChange.order_id = #{orderId}
and dtlChange.order_dtl_id = #{orderDtlId}
and ${queryStr}
order by ${orderStr}
