mapper.delete:
        delete from vmes_sale_order
        where id in

		#{item}

mapper.select:
select id id, company_id companyId, order_number orderNumber, sys_code sysCode, receipt_type receiptType,
price_type priceType, state state, make_id makeId, customer_id customerId, order_date orderDate,
deliver_date deliverDate, total_sum totalSum, discount_sum discountSum, order_sum orderSum, advance_sum advanceSum,
receivables receivables, cuser cuser, cdate cdate, uuser uuser, udate udate,
remark remark, isdisable isdisable from vmes_sale_order
and id <> #{id}
and id = #{id}
and company_id = #{currentCompanyId}
and sys_code = #{sysCode}
and order_number = #{orderNumber}
and ${queryStr}
order by ${orderStr}

select saleOrder.id id, saleOrder.id parentId, saleOrder.company_id companyId, saleOrder.order_number orderNumber, saleOrder.sys_code sysCode, saleOrder.receipt_type receiptType, CASE WHEN saleOrder.receipt_type = '1' THEN '不开票' WHEN saleOrder.receipt_type = '2' THEN '普票发票' WHEN saleOrder.receipt_type = '3' THEN '增值税发票' ELSE null END receiptTypeName,
saleOrder.price_type priceType, saleOrder.state state, CASE WHEN saleOrder.state = '0' THEN '待提交' WHEN saleOrder.state = '1' THEN '待审核' WHEN saleOrder.state = '2' THEN '待出库' WHEN saleOrder.state = '3' THEN '待发货' WHEN saleOrder.state = '4' THEN '已发货' WHEN saleOrder.state = '5' THEN '已完成' WHEN saleOrder.state = '-1' THEN '已取消' ELSE null END stateName, saleOrder.make_id makeId, saleOrder.customer_id customerId, DATE_FORMAT(saleOrder.order_date,'%Y-%m-%d') orderDate,
DATE_FORMAT(saleOrder.deliver_date,'%Y-%m-%d') deliverDate, round(ifnull(saleOrder.total_sum, 0),2) totalSum, round(ifnull(saleOrder.discount_sum, 0),2) discountSum, round(ifnull(saleOrder.order_sum, 0),2) orderSum, round(ifnull(saleOrder.advance_sum, 0),2) advanceSum,
round(ifnull(saleOrder.receivables, 0),2) receivables, saleOrder.cuser cuser, DATE_FORMAT(saleOrder.cdate,'%Y-%m-%d %T') cdate, saleOrder.uuser uuser, DATE_FORMAT(saleOrder.udate,'%Y-%m-%d %T') udate,
saleOrder.remark remark, saleOrder.isdisable isdisable,
makeUser.user_name makeName, customer.name customerName from vmes_sale_order saleOrder left join vmes_customer customer on saleOrder.customer_id = customer.id left join vmes_user makeUser on saleOrder.make_id = makeUser.id
and saleOrder.company_id = #{currentCompanyId}
and saleOrder.state = #{state}
and saleOrder.sys_code = #{sysCode}
and saleOrder.order_number = #{orderNumber}
and saleOrder.price_type = #{priceType}
and ${queryStr}
order by ${orderStr}
