mapper.select:
select

from






                 and id <> #{id} 


                and id = #{id}







        and ${queryStr}



    order by ${orderStr}

select

from




        and ${queryStr}


        and code LIKE CONCAT(CONCAT('%', #{code}),'%')


        and name LIKE CONCAT(CONCAT('%', #{name}),'%')



    order by ${orderStr}

select

from




        and ${queryStr}


        and code LIKE CONCAT(CONCAT('%', #{code}),'%')


        and name LIKE CONCAT(CONCAT('%', #{name}),'%')



    order by ${orderStr}

select saleOrder.id, saleOrder.customer_id customerId, saleOrder.sys_code sysCode, DATE_FORMAT(saleOrder.order_date,'%Y-%m-%d') orderDate, round(ifnull(saleOrder.order_sum, 0),2) orderSum, CASE WHEN saleOrder.state = '0' THEN '待提交' WHEN saleOrder.state = '1' THEN '待审核' WHEN saleOrder.state = '2' THEN '待发货' WHEN saleOrder.state = '3' THEN '已发货' WHEN saleOrder.state = '4' THEN '已完成' WHEN saleOrder.state = '-1' THEN '已取消' ELSE null END state, round(sum(ifnull(detail.discount_amount, 0)),2) discountedAmount, round(sum(ifnull(detail.receive_amount, 0)),2) receivedAmount, round(ifnull(saleOrder.order_sum, 0)-sum(ifnull(detail.discount_amount, 0))-sum(ifnull(detail.receive_amount, 0)),2) noReceiveAmount, 0.00 discountAmount, 0.00 receiveAmount from vmes_sale_order saleOrder left join vmes_customer customer on saleOrder.customer_id = customer.id left join vmes_sale_receive_detail detail on detail.order_id = saleOrder.id and detail.state = '1'
and ${queryStr}
and saleOrder.sys_code LIKE CONCAT(CONCAT('%', #{sysCode}),'%')
and saleOrder.customer_id = #{customerId}
and saleOrder.company_id = #{currentCompanyId}
group by saleOrder.id,saleOrder.customer_id,saleOrder.sys_code,saleOrder.order_date,saleOrder.order_sum,saleOrder.state
order by ${orderStr}
