mapper.update:
update
set isdisable = '0' ,udate = now() where id in
#{item}

mapper.select:
select

from






                 and id <> #{id} 


                and id = #{id}







        and ${queryStr}



    order by ${orderStr}

select detail.id, detail.order_id orderId, detail.parent_id parentId, case when detail.state='0' then '待收款' when detail.state='1' then '已收款' else '已取消' end state, saleorder.sys_code sysCode, round(ifnull(saleorder.order_sum,0),2) orderSum, round(ifnull(detail.discount_amount,0),2) discountAmount, round(ifnull(detail.receive_amount,0),2) receiveAmount, group_concat(product.name order by product.name asc) productName from vmes_sale_receive_detail detail left join vmes_sale_order saleorder on saleorder.id = detail.order_id left join vmes_sale_order_detail orderdetail on orderdetail.parent_id = saleorder.id left join vmes_product product on product.id = orderdetail.product_id
and ${queryStr}
and saleorder.sys_code LIKE CONCAT(CONCAT('%', #{sysCode}),'%')
and product.name LIKE CONCAT(CONCAT('%', #{productName}),'%')
and detail.parent_id = #{parentId}
group by detail.id,detail.parent_id,saleorder.sys_code,saleorder.order_sum,detail.discount_amount,detail.receive_amount
order by ${orderStr}

select detail.id, detail.order_id orderId, detail.parent_id parentId, case when detail.state='0' then '待收款' when detail.state='1' then '已收款' else '已取消' end state, saleorder.sys_code sysCode, round(ifnull(saleorder.order_sum,0),2) orderSum, round(ifnull(detail.discount_amount,0),2) discountAmount, round(ifnull(detail.receive_amount,0),2) receiveAmount, group_concat(product.name order by product.name asc) productName from vmes_sale_receive_detail detail left join vmes_sale_order saleorder on saleorder.id = detail.order_id left join vmes_sale_order_detail orderdetail on orderdetail.parent_id = saleorder.id left join vmes_product product on product.id = orderdetail.product_id
and ${queryStr}
and saleorder.sys_code LIKE CONCAT(CONCAT('%', #{sysCode}),'%')
and product.name LIKE CONCAT(CONCAT('%', #{productName}),'%')
and detail.parent_id = #{parentId}
group by detail.id,detail.parent_id,saleorder.sys_code,saleorder.order_sum,detail.discount_amount,detail.receive_amount
order by ${orderStr}

select saleOrder.id, saleOrder.customer_id customerId, saleOrder.sys_code sysCode, DATE_FORMAT(saleOrder.order_date,'%Y-%m-%d') orderDate, round(ifnull(saleOrder.order_sum, 0),2) orderSum, CASE WHEN saleOrder.state = '0' THEN '待提交' WHEN saleOrder.state = '1' THEN '待审核' WHEN saleOrder.state = '2' THEN '待发货' WHEN saleOrder.state = '3' THEN '已发货' WHEN saleOrder.state = '4' THEN '已完成' WHEN saleOrder.state = '-1' THEN '已取消' ELSE null END state, round(sum(ifnull(detail.discount_amount, 0)),2) discountedAmount, round(sum(ifnull(detail.receive_amount, 0)),2) receivedAmount, round(ifnull(saleOrder.order_sum, 0)-sum(ifnull(detail.discount_amount, 0))-sum(ifnull(detail.receive_amount, 0)),2) noReceiveAmount, 0.00 discountAmount, 0.00 receiveAmount from vmes_sale_order saleOrder left join vmes_customer customer on saleOrder.customer_id = customer.id left join vmes_sale_receive_detail detail on detail.order_id = saleOrder.id and detail.state = '1'
and saleOrder.sys_code LIKE CONCAT(CONCAT('%', #{sysCode}),'%')
and saleOrder.customer_id = #{customerId}
and saleOrder.company_id = #{currentCompanyId}
and saleOrder.order_date >= #{orderDateBegin}
and saleOrder.order_date <= #{orderDateEnd}
and ${queryStr}
group by saleOrder.id,saleOrder.customer_id,saleOrder.sys_code,saleOrder.order_date,saleOrder.order_sum,saleOrder.state
order by ${orderStr}

select receive.order_id orderId, round(ifnull(receive.receive_amount, 0),2) receiveAmount, round(ifnull(receive.discount_amount, 0),2) discountAmount, round(ifnull((receive.receive_amount + receive.discount_amount), 0), 2) receiveSum,
round(ifnull(saleOrder.total_sum, 0), 2) orderTotalSum, round(ifnull(saleOrder.order_sum, 0), 2) orderSum from ( select order_id, sum(receive_amount) receive_amount, sum(discount_amount) discount_amount from vmes_sale_receive_detail
and order_id = #{orderId}
and order_id in (${orderIds})
and state = #{state}
group by order_id ) receive left join vmes_sale_order saleOrder on receive.order_id = saleOrder.id
