mapper.update:
update
set isdisable = '0' ,udate = now() where id in
#{item}

mapper.select:
select

from






                 and id <> #{id} 


                and id = #{id}







        and ${queryStr}



    order by ${orderStr}

select detail.id, detail.order_id orderId, detail.parent_id parentId, case when detail.state='0' then '待收款' when detail.state='1' then '已收款' else '已取消' end state, saleorder.sys_code sysCode, round(ifnull(saleorder.order_sum,0),2) orderSum, round(ifnull(detail.discount_amount,0),2) discountAmount, round(ifnull(detail.receive_amount,0),2) receiveAmount, group_concat(product.name order by product.name asc) productName from vmes_sale_receive_detail detail left join vmes_sale_order saleorder on saleorder.id = detail.order_id left join vmes_sale_order_detail orderdetail on orderdetail.parent_id = saleorder.id left join vmes_product product on product.id = orderdetail.product_id
and ${queryStr}
and saleorder.sys_code LIKE CONCAT(CONCAT('%', #{sysCode}),'%')
and product.name LIKE CONCAT(CONCAT('%', #{productName}),'%')
and detail.parent_id = #{parentId}
group by detail.id,detail.parent_id,saleorder.sys_code,saleorder.order_sum,detail.discount_amount,detail.receive_amount
order by ${orderStr}

select detail.id, detail.order_id orderId, detail.parent_id parentId, case when detail.state='0' then '待收款' when detail.state='1' then '已收款' else '已取消' end state, saleorder.sys_code sysCode, receive.code code, round(ifnull(saleorder.order_sum,0),2) orderSum, round(ifnull(detail.discount_amount,0),2) discountAmount, round(ifnull(detail.receive_amount,0),2) receiveAmount, group_concat(product.name order by product.name asc) productName from vmes_sale_receive_detail detail left join vmes_sale_receive receive on receive.id = detail.parent_id left join vmes_sale_order saleorder on saleorder.id = detail.order_id left join vmes_sale_order_detail orderdetail on orderdetail.parent_id = saleorder.id left join vmes_product product on product.id = orderdetail.product_id
and ${queryStr}
and saleorder.sys_code LIKE CONCAT(CONCAT('%', #{sysCode}),'%')
and product.name LIKE CONCAT(CONCAT('%', #{productName}),'%')
and detail.parent_id = #{parentId}
and detail.order_id = #{orderId}
group by detail.id,detail.parent_id,saleorder.sys_code,saleorder.order_sum,detail.discount_amount,detail.receive_amount
order by ${orderStr}

select id,sysCode,customerId,companyId,currentDeliverDate,orderAmount,orderDate,state,
CASE
WHEN state = '0' THEN '待提交'
WHEN state = '1' THEN '待审核'
WHEN state = '2' THEN '待发货'
WHEN state = '3' THEN '已发货'
WHEN state = '4' THEN '已完成'
WHEN state = '-1' THEN '已取消'
ELSE null
END  orderState,
round(sum(ifnull(deliverAmount,0)),2) deliverAmount,round(sum(ifnull(receiveAmount,0)),2) receivedAmount,round(sum(ifnull(discountAmount,0)),2) discountedAmount,'0.00' discountAmount,
case when sum(ifnull(deliverAmount,0)) - sum(ifnull(retreatAmount,0))  >=  (sum(ifnull(receiveAmount,0))+sum(ifnull(discountAmount,0))) then round(sum(ifnull(deliverAmount,0)) - sum(ifnull(retreatAmount,0)) - sum(ifnull(receiveAmount,0)) - sum(ifnull(discountAmount,0)),2) else 0.00 end nowReceiveAmount,
case when sum(ifnull(deliverAmount,0)) - sum(ifnull(retreatAmount,0))  <  (sum(ifnull(receiveAmount,0))+sum(ifnull(discountAmount,0))) then round(sum(ifnull(receiveAmount,0)) + sum(ifnull(retreatAmount,0)) - sum(ifnull(deliverAmount,0)) + sum(ifnull(discountAmount,0)),2) else 0.00 end preReceiveAmount
from (
    select saleOrder.id id,saleOrder.sys_code sysCode,saleOrder.customer_id customerId,saleOrder.current_deliver_date currentDeliverDate,saleOrder.company_id companyId,saleOrder.order_sum orderAmount,saleOrder.state state,DATE_FORMAT(saleOrder.order_date,'%Y-%m-%d') orderDate,
    deliverDetail.sum deliverAmount,null receiveAmount,null discountAmount,null retreatAmount
    from vmes_sale_order saleOrder
    left join vmes_sale_deliver_detail deliverDetail on deliverDetail.order_id = saleOrder.id

        and deliverDetail.state = '1'

            and saleOrder.company_id = #{currentCompanyId}


            and saleOrder.customer_id = #{customerId}


    union all
    select saleOrder.id id,saleOrder.sys_code sysCode,saleOrder.customer_id customerId,saleOrder.current_deliver_date currentDeliverDate,saleOrder.company_id companyId,saleOrder.order_sum orderAmount,saleOrder.state state,DATE_FORMAT(saleOrder.order_date,'%Y-%m-%d') orderDate,
    null deliverAmount,receiveDetail.receive_amount receiveAmount,receiveDetail.discount_amount discountAmount,null retreatAmount
    from vmes_sale_order saleOrder
    left join vmes_sale_receive_detail receiveDetail on receiveDetail.order_id = saleOrder.id

        and receiveDetail.state = '1'

            and saleOrder.company_id = #{currentCompanyId}


            and saleOrder.customer_id = #{customerId}


    union all
    select saleOrder.id id,saleOrder.sys_code sysCode,saleOrder.customer_id customerId,saleOrder.current_deliver_date currentDeliverDate,saleOrder.company_id companyId,saleOrder.order_sum orderAmount,saleOrder.state state,DATE_FORMAT(saleOrder.order_date,'%Y-%m-%d') orderDate,
    null deliverAmount,null receiveAmount,null discountAmount,ifnull(retreatDetail.order_count,0)*ifnull(orderDtail.product_price,0) retreatAmount
    from vmes_sale_order saleOrder
    left join vmes_sale_order_detail orderDtail on saleOrder.id = orderDtail.parent_id
    left join vmes_sale_retreat_detail retreatDetail on orderDtail.id = retreatDetail.order_detail_id

        and retreatDetail.state = '3'

            and saleOrder.company_id = #{currentCompanyId}


            and saleOrder.customer_id = #{customerId}


)t


        and t.orderCode LIKE CONCAT(CONCAT('%', #{orderCode}),'%')


        and t.customerId = #{customerId}


        and t.companyId = #{currentCompanyId}


        and t.state not in (${orderStateNotin})


        and t.state in (${orderState})



         and t.orderDate >= #{orderDateBegin} 


         and t.orderDate <= #{orderDateEnd} 




        and ${queryStr}


group by t.id


    having ${havingStr}


    order by ${orderStr}

select receive.order_id orderId, round(ifnull(receive.receive_amount, 0),2) receiveAmount, round(ifnull(receive.discount_amount, 0),2) discountAmount, round((ifnull(receive.receive_amount, 0) + ifnull(receive.discount_amount, 0)), 2) receiveSum,
round(ifnull(saleOrder.total_sum, 0), 2) orderTotalSum, round(ifnull(saleOrder.order_sum, 0), 2) orderSum from ( select order_id, sum(receive_amount) receive_amount, sum(discount_amount) discount_amount from vmes_sale_receive_detail
and order_id = #{orderId}
and order_id in (${orderIds})
and state = #{state}
group by order_id ) receive left join vmes_sale_order saleOrder on receive.order_id = saleOrder.id
