mapper.select:
select detail.id id, detail.parent_id parentId, detail.deliver_detail_id deliverDetailId, detail.order_detail_id orderDetailId, detail.product_id productId,
round(ifnull(detail.order_sum, 0),2) orderSum, detail.order_unit orderUnit, orderUnit.name orderUnitName, round(ifnull(detail.order_count, 0),2) orderCount, detail.product_unit productUnit, detail.product_count productCount,
detail.state state, CASE WHEN detail.state = '0' THEN '待提交' WHEN detail.state = '1' THEN '待审核' WHEN detail.state = '2' THEN '待退款' WHEN detail.state = '3' THEN '已完成' WHEN detail.state = '-1' THEN '已取消' ELSE null END stateName, detail.cuser cuser, detail.cdate cdate, detail.uuser uuser, detail.udate udate,
detail.remark remark, detail.isdisable isdisable,
prodUnitPrice.np_formula n2pFormula, prodUnitPrice.pn_formula p2nFormula, round(ifnull(prodUnitPrice.product_price, 0), 2) productPrice,
product.code productCode, product.name productName, product.spec productSpec, product.genre productGenre, genre.name productGenreName,
saleOrder.sys_code sysOrderCode, deliver.deliver_code deliverCode, round(ifnull(receive.receive_amount, 0), 2) receiveAmount,
round(ifnull(deliverDetail.order_count, 0), 2) deliverOrderCount
,round(ifnull(retreatDetailByEdit.order_count, 0), 2) endRetreatCount
,round((ifnull(deliverDetail.order_count, 0) - ifnull(retreatDetailByEdit.order_count, 0)), 2) notRetreatCount
,round(ifnull(retreatSumByEdit.order_sum, 0), 2) endRetreatSum ,round((ifnull(receive.receive_amount, 0) - ifnull(retreatSumByEdit.order_sum, 0)), 2) notRetreatSum
,round(ifnull(retreatDetailByInfo.order_count, 0), 2) endRetreatCount
,round((ifnull(deliverDetail.order_count, 0) - ifnull(retreatDetailByInfo.order_count, 0)), 2) notRetreatCount
,round(ifnull(retreatSumByInfo.order_sum, 0), 2) endRetreatSum ,round((ifnull(receive.receive_amount, 0) - ifnull(retreatSumByInfo.order_sum, 0)), 2) notRetreatSum
from vmes_sale_retreat_detail detail left join vmes_sale_order_detail orderDetail on detail.order_detail_id = orderDetail.id left join vmes_sale_order saleOrder on orderDetail.parent_id = saleOrder.id
left join vmes_sale_deliver_detail deliverDetail on detail.deliver_detail_id = deliverDetail.id left join vmes_sale_deliver deliver on deliverDetail.parent_id = deliver.id
left join vmes_dictionary orderUnit on detail.order_unit = orderUnit.id left join vmes_product product on detail.product_id = product.id left join vmes_dictionary genre on product.genre = genre.id left join vmes_product_unit prodUnitPrice on (detail.product_id = prodUnitPrice.product_id and detail.order_unit = prodUnitPrice.unit and prodUnitPrice.isdisable = '1')
left join ( select deliver_detail_id, sum(order_count) order_count from vmes_sale_retreat_detail where id not in ( select id from vmes_sale_retreat_detail
and parent_id = #{parentId}
) and state <> '-1' group by deliver_detail_id ) retreatDetailByEdit on detail.deliver_detail_id = retreatDetailByEdit.deliver_detail_id
left join ( select c.id order_id,sum(a.order_sum) order_sum from vmes_sale_retreat_detail a left join vmes_sale_order_detail b on a.order_detail_id = b.id left join vmes_sale_order c on b.parent_id = c.id where a.id not in ( select id from vmes_sale_retreat_detail
and parent_id = #{parentId}
) and a.state <> '-1' group by c.id ) retreatSumByEdit on orderDetail.parent_id = retreatSumByEdit.order_id
left join ( select deliver_detail_id, sum(order_count) order_count from vmes_sale_retreat_detail where parent_id in ( select id from vmes_sale_retreat
and company_id = #{currentCompanyId}
) and state <> '-1' group by deliver_detail_id ) retreatDetailByInfo on detail.deliver_detail_id = retreatDetailByInfo.deliver_detail_id
left join ( select c.id order_id,sum(a.order_sum) order_sum from vmes_sale_retreat_detail a left join vmes_sale_order_detail b on a.order_detail_id = b.id left join vmes_sale_order c on b.parent_id = c.id where a.parent_id in ( select id from vmes_sale_retreat
and company_id = #{currentCompanyId}
) and a.state <> '-1' group by c.id ) retreatSumByInfo on orderDetail.parent_id = retreatSumByInfo.order_id
left join ( select order_id, sum(receive_amount) receive_amount, sum(discount_amount) discount_amount from vmes_sale_receive_detail where state = '1' group by order_id ) receive on orderDetail.parent_id = receive.order_id
and detail.parent_id = #{parentId}
and ${queryStr}
order by ${orderStr}
