mapper.delete:
        delete from vmes_sale_retreat_detail
        where id in

		#{item}

mapper.update:
update vmes_sale_retreat_detail set
state = #{state},
udate = now()


        and parent_id = #{parentId}


        and ${parentIds}

mapper.select:
select id id, parent_id parentId, deliver_detail_id deliverDetailId, order_detail_id orderDetailId, in_detail_id inDetailId,
product_id productId, order_sum orderSum, order_unit orderUnit, order_count orderCount, product_unit productUnit,
product_count productCount, state state, cuser cuser, cdate cdate, uuser uuser,
udate udate, remark remark, isdisable isdisable from vmes_sale_retreat_detail detail
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select detail.id id, detail.parent_id parentId, detail.deliver_detail_id deliverDetailId, detail.order_detail_id orderDetailId, detail.product_id productId,
round(ifnull(detail.order_sum, 0),2) orderSum, detail.order_unit orderUnit, round(ifnull(detail.order_count, 0),2) orderCount, detail.product_unit productUnit, detail.product_count productCount,
detail.state state, CASE WHEN detail.state = '0' THEN '待提交' WHEN detail.state = '1' THEN '待审核' WHEN detail.state = '2' THEN '待退款' WHEN detail.state = '3' THEN '已完成' WHEN detail.state = '-1' THEN '已取消' ELSE null END stateName, detail.cuser cuser, detail.cdate cdate, detail.uuser uuser, detail.udate udate,
detail.remark remark, detail.isdisable isdisable,
product.code productCode, product.name productName, product.spec productSpec, product.genre productGenre, genre.name productGenreName, product.type productType,
orderUnit.name orderUnitName from vmes_sale_retreat_detail detail left join vmes_dictionary orderUnit on detail.order_unit = orderUnit.id left join vmes_product product on detail.product_id = product.id left join vmes_dictionary genre on product.genre = genre.id
and detail.parent_id = #{parentId}
and ${queryStr}
order by ${orderStr}
