mapper.delete:
        delete from vmes_sale_retreat
        where id in

		#{item}

delete from vmes_sale_retreat_detail
where parent_id in (
    select id from vmes_sale_retreat


            and company_id = #{companyId}


)

mapper.select:
select id id, company_id companyId, customer_id customerId, sys_code sysCode, state state,
total_sum totalSum, retreat_date retreatDate, cuser cuser, cdate cdate, uuser uuser,
udate udate, remark remark, isdisable isdisable from vmes_sale_retreat
and id <> #{id}
and id = #{id}
and ${queryStr}
order by ${orderStr}

select retreat.id id, retreat.id parentId, retreat.company_id companyId, retreat.customer_id customerId, retreat.sys_code sysCode, retreat.state state, CASE WHEN retreat.state = '0' THEN '待提交' WHEN retreat.state = '1' THEN '待审核' WHEN retreat.state = '2' THEN '待退款' WHEN retreat.state = '3' THEN '已完成' WHEN retreat.state = '-1' THEN '已取消' ELSE null END stateName,
round(ifnull(retreat.total_sum, 0),2) totalSum, retreat.retreat_date retreatDate, DATE_FORMAT(retreat.retreat_date,'%Y-%m-%d') retreatDateStr, retreat.cuser cuser, retreat.cdate cdate, DATE_FORMAT(retreat.cdate,'%Y-%m-%d') cdateStr, retreat.uuser uuser,
retreat.udate udate, retreat.remark remark, retreat.isdisable isdisable,
customer.name customerName
from vmes_sale_retreat retreat left join vmes_customer customer on retreat.customer_id = customer.id
and retreat.company_id = #{currentCompanyId}
and retreat.sys_code = #{sysCode}
and retreat.state = #{state}
and customer.name LIKE CONCAT(CONCAT(#{customerName}),'%')
and retreat.cdate >= #{cdateBegin}
and retreat.cdate <= #{cdateEnd}
and ${queryStr}
order by ${orderStr}
