cuser cuser, cdate cdate, uuser uuser, udate udate, remark remark,
isdisable isdisable, check_stock_count checkStockCount, in_dtl_id inDtlId, out_dtl_id outDtlId from vmes_warehouse_check_detail
where 1=2
and id <> #{id}
and id = #{id}
and parent_id = #{parentId}
and ${queryStr}
order by ${orderStr}
