mapper.delete:
delete from vmes_warehouse_check_detail
where id in

    #{item}

mapper.select:
select id id, parent_id parentId, qrcode qrcode, execute_id executeId, warehouse_product_id warehouseProductId,
warehouse_id warehouseId, product_id productId, code code, stock_count stockCount, state state,
cuser cuser, cdate cdate, uuser uuser, udate udate, remark remark,
isdisable isdisable from vmes_warehouse_check_detail
where 1=2
and id <> #{id}
and id = #{id}
and parent_id = #{parentId}
and ${queryStr}
order by ${orderStr}

select detail.id id, detail.parent_id parentId, detail.qrcode qrcode, executor.executor_id executorId, user.user_code executorUserCode, user.user_name executorUserName,
detail.warehouse_product_id warehouseProductId, detail.warehouse_id warehouseId, detail.product_id productId, detail.code code, round(ifnull(wp.stock_count, 0), 2) stockCount,
detail.state state, CASE WHEN detail.state = '0' THEN '待派单' WHEN detail.state = '1' THEN '执行中' WHEN detail.state = '2' THEN '审核中' WHEN detail.state = '3' THEN '已完成' WHEN detail.state = '-1' THEN '已取消' ELSE null END stateName,
detail.cuser cuser, DATE_FORMAT(detail.cdate,'%Y-%m-%d %T') cdate, detail.uuser uuser, DATE_FORMAT(detail.udate,'%Y-%m-%d %T') udate, detail.remark remark, detail.isdisable isdisable,
checkExecute.id executeId, checkExecute.state executeState,
warehouse.path_name pathName, warehouse.code warehouseCode,
product.code productCode, product.name productName, product.spec productSpec, produnit.unit productUnit, product.genre productGenre, product.type productType, unit.name productUnitName, genre.name productGenreName
from vmes_warehouse_check_detail detail left join vmes_warehouse warehouse on detail.warehouse_id = warehouse.id and warehouse.isdisable = '1' left join vmes_product product on detail.product_id = product.id and product.isdisable = '1' left join vmes_product_unit produnit on produnit.product_id = product.id and produnit.type = 1 left join vmes_dictionary unit on produnit.unit = unit.id left join vmes_dictionary genre on product.genre = genre.id left join vmes_warehouse_check_executor executor on detail.id = executor.detail_id and executor.isdisable = '1' left join vmes_user user on executor.executor_id = user.id left join vmes_warehouse_check_execute checkExecute on detail.id = checkExecute.detail_id and checkExecute.isdisable = '1' left join vmes_warehouse_product wp on detail.warehouse_product_id = wp.id
and detail.parent_id = #{parentId}
and detail.state = #{state}
and ${queryStr}
order by ${orderStr}

select detail.product_id productId, detail.id detailId, wcheck.id parentId, warehouse.path_name pathName, product.code productCode, product.name productName, product.spec spec, genre.name genreName, unit.name unitName, detail.code code, round(ifnull(detail.stock_count, 0), 2) stockCount
from vmes_warehouse_check_detail detail left join vmes_warehouse_check wcheck on wcheck.id = detail.parent_id left join vmes_product product on detail.product_id = product.id left join vmes_product_unit produnit on produnit.product_id = product .id and produnit.type = 1 left join vmes_dictionary unit on produnit.unit = unit.id left join vmes_dictionary genre on product.genre = genre.id left join vmes_warehouse warehouse on warehouse.id = detail.warehouse_id
and detail.id = #{detailId}
