mapper.delete:
delete from vmes_warehouse_check_execute
where id in

    #{item}

mapper.update:
update vmes_warehouse_check_execute set
isdisable = #{isdisable},
udate = now()


        and parent_id = #{parentId}


        and detail_id = #{detailId}


        and executor_id = #{executorId}

mapper.select:
select
    id id,
    parent_id parentId,
    detail_id detailId,
    executor_id executorId,
    audit_id auditId,

    count count,
    state state,
    cuser cuser,
    cdate cdate,
    uuser uuser,

    udate udate,
    remark remark,
    isdisable isdisable
from vmes_warehouse_check_execute



        where 1=2







                         and id <> #{id} 


                        and id = #{id}





                and parent_id = #{parentId}


                and detail_id = #{detailId}


                and executor_id = #{executorId}


                and audit_id = #{auditId}


                and state = #{state}


                and isdisable = #{isdisable}




                and ${queryStr}





    order by ${orderStr}
