mapper.delete:
delete from vmes_warehouse_check
where id in

    #{item}

mapper.select:
select
id id,
company_id companyId,
code code,
type type,
make_id makeId,

state state,
cuser cuser,
cdate cdate,
uuser uuser,
udate udate,

remark remark,
isdisable isdisable
from vmes_warehouse_check



        where 1=2







                         and id <> #{id} 


                        and id = #{id}




                and code = #{code}


                and company_id = #{companyId}


                and type = #{type}




                and ${queryStr}





    order by ${orderStr}

select check.id id, check.company_id companyId, check.code code, check.type type, CASE WHEN check.type = '2' THEN '按货品' WHEN check.type = '1' THEN '按库位' ELSE null END typeName, check.make_id makeId, user.user_name makeName,
check.state state, CASE WHEN check.state = '0' THEN '未完成' WHEN check.state = '1' THEN '已完成' WHEN check.state = '-1' THEN '已取消' ELSE null END stateName, check.cuser cuser, DATE_FORMAT(check.cdate,'%Y-%m-%d %T') cdate, check.uuser uuser, DATE_FORMAT(check.udate,'%Y-%m-%d %T') udate,
check.remark remark, check.isdisable isdisable from vmes_warehouse_check check left join vmes_user user on a.make_id = user.id
and code = #{code}
and company_id = #{companyId}
and type = #{type}
and ${queryStr}
order by ${orderStr}
