mapper.delete:
delete from vmes_warehouse_check
where id in

    #{item}

delete from vmes_warehouse_check_detail
where parent_id in (
    select id from vmes_warehouse_check


            and company_id = #{companyId}


)

delete from vmes_warehouse_check_executor
where parent_id in (
    select id from vmes_warehouse_check


            and company_id = #{companyId}


)

delete from vmes_warehouse_check_execute
where parent_id in (
    select id from vmes_warehouse_check


            and company_id = #{companyId}


)

mapper.select:
select id id, company_id companyId, code code, type type, make_id makeId,
state state, is_simple isSimple, cuser cuser, cdate cdate, uuser uuser,
udate udate, remark remark, isdisable isdisable, warehouse_attribute warehouseAttribute from vmes_warehouse_check
where 1=2
and id <> #{id}
and id = #{id}
and code = #{code}
and company_id = #{companyId}
and type = #{type}
and ${queryStr}
order by ${orderStr}

select distinct parent.id id, parent.id parentId, parent.company_id companyId, parent.code code, parent.type type, CASE WHEN parent.type = '2' THEN '按货品' WHEN parent.type = '1' THEN '按库位' ELSE null END typeName, parent.make_id makeId, user.user_name makeName,
parent.state state, CASE WHEN parent.state = '0' THEN '未完成' WHEN parent.state = '1' THEN '已完成' WHEN parent.state = '-1' THEN '已取消' ELSE null END stateName, parent.is_simple isSimple, parent.cuser cuser, DATE_FORMAT(parent.cdate,'%Y-%m-%d') cdate, DATE_FORMAT(parent.cdate,'%Y-%m-%d') cdateStr, parent.uuser uuser,
DATE_FORMAT(parent.udate,'%Y-%m-%d') udate, parent.remark remark, parent.isdisable isdisable from vmes_warehouse_check parent left join vmes_user user on parent.make_id = user.id
left join vmes_warehouse_check_detail detail on parent.id = detail.parent_id left join vmes_product prod on detail.product_id = prod.id left join vmes_product_unit produnit on produnit.product_id = prod.id and produnit.type = 1 left join vmes_dictionary genre on prod.genre = genre.id left join vmes_dictionary unit on produnit.unit = unit.id
and parent.code = #{code}
and parent.company_id = #{currentCompanyId}
and parent.state = #{state}
and parent.type = #{type}
and parent.cdate >= #{createDateBegin}
and parent.cdate <= #{createDateEnd}
and detail.state = #{detailState}
and prod.code LIKE CONCAT(CONCAT(#{productCode}),'%')
and prod.name LIKE CONCAT(CONCAT(#{productName}),'%')
and prod.spec LIKE CONCAT(CONCAT(#{productSpec}),'%')
and genre.name LIKE CONCAT(CONCAT(#{productGenreName}),'%')
and unit.name LIKE CONCAT(CONCAT(#{productUnitName}),'%')
and parent.id in ( select parent_id from vmes_warehouse_check_executor
and executor_id = #{cuser}
and isdisable = '1'
group by parent_id )
and parent.id in ( select parent_id from vmes_warehouse_check_execute where isdisable = '1' and state in ('0','2','3') group by parent_id )
and ${queryStr}
order by ${orderStr}
