mapper.delete:
        delete from vmes_warehouse_in_detail
        where id in

		#{item}

mapper.select:
select id id, parent_id parentId, qrcode qrcode, business_id businessId, execute_id executeId,
warehouse_id warehouseId, product_id productId, code code, count count, state state,
cuser cuser, cdate cdate, uuser uuser, udate udate, remark remark,
isdisable isdisable from vmes_warehouse_in_detail
where 1=2
and id <> #{id}
and id = #{id}
and parent_id = #{parentId}
and code = #{code}
and product_id = #{productId}
and warehouse_id = #{warehouseId}
and ${queryStr}
order by ${orderStr}

select id,parentId,qrcode,businessId,executeId,executeName, warehouseId,warehouseName,productId,code,count,state,stateName, cuser,cdate,uuser,udate,remark, isdisable,
productCode,productName,productSpec,productUnit,productUnitName,productGenre,productGenreName,productType,
parentCode,makeId,
round(sum(ifnull(executeCount,0)),2) executeCount,
round((count - sum(ifnull(executeCount,0))), 2) surplusCount from ( select a.id id, a.parent_id parentId, a.qrcode qrcode, a.business_id businessId, a.execute_id executeId, '' executeName,
a.warehouse_id warehouseId, warehouse.path_name warehouseName, a.product_id productId, a.code code, ROUND(a.count, 2) count, a.state state, CASE WHEN a.state = '0' THEN '待派单' WHEN a.state = '1' THEN '执行中' WHEN a.state = '2' THEN '已完成' WHEN a.state = '-1' THEN '已取消' ELSE null END stateName,
a.cuser cuser, DATE_FORMAT(a.cdate,'%Y-%m-%d %T') cdate, a.uuser uuser, DATE_FORMAT(a.udate,'%Y-%m-%d %T') udate, a.remark remark,
a.isdisable isdisable,
prod.code productCode, prod.name productName, prod.spec productSpec, produnit.unit productUnit, unit.name productUnitName, prod.genre productGenre, genre.name productGenreName, prod.type productType,
parent.code parentCode, parent.make_id makeId,
inexecute.count executeCount from vmes_warehouse_in_detail a left join vmes_warehouse warehouse on a.warehouse_id = warehouse.id left join vmes_warehouse_in parent on a.parent_id = parent.id left join vmes_product prod on a.product_id = prod.id left join vmes_product_unit produnit on produnit.product_id = prod.id and produnit.type = 1 left join vmes_dictionary unit on produnit.unit = unit.id left join vmes_dictionary genre on prod.genre = genre.id
left join vmes_warehouse_in_execute inexecute on a.id = inexecute.detail_id and inexecute.isdisable = '1'
and a.parent_id = #{parentId}
and a.state = #{state}
and a.id in ( select detail_id from vmes_warehouse_in_executor
and executor_id = #{cuser}
and isdisable = '1'
)
and ${queryStr}
order by ${orderStr}
) in_detail group by id

select detail.id detailId, win.id parentId, type.name typeType, win.dept_name deptName, round(detail.count,2) taskCount, round(sum(ifnull(execute.count,0)),2) taskDone, round((round(detail.count,2)-(sum(ifnull(execute.count,0)))),2) taskLeft, product.code productCode, product.name productName, product.spec spec, genre.name genreName, unit.name unitName, detail.code pathCode, warehouse.path_name pathName
from vmes_warehouse_in_detail detail left join vmes_warehouse_in win on win.id = detail.parent_id left join vmes_dictionary type on win.type = type.id left join vmes_warehouse_in_execute execute on execute.detail_id = detail.id left join vmes_product product on detail.product_id = product.id left join vmes_product_unit produnit on produnit.product_id = product .id and produnit.type = 1 left join vmes_dictionary unit on produnit.unit = unit.id left join vmes_dictionary genre on product.genre = genre.id left join vmes_warehouse warehouse on warehouse.id = detail.warehouse_id
and detail.id = #{detailId}
